class HermitJson {
    private $base_url = 'https://api.xiami.com/web';

    public function xiami_http(int $type, string $id): array|bool {
        $endpoints = [
            0 => '/v1/song/getInfo',
            1 => '/v1/album/getInfo',
            2 => '/v1/collection/getInfo'
        ];

        if (!isset($endpoints[$type])) {
            return false;
        }

        $url = $this->base_url . $endpoints[$type];
        $params = [
            'id' => $id
        ];

        $ch = curl_init();
        curl_setopt($ch, CURLOPT_URL, $url);
        curl_setopt($ch, CURLOPT_POST, true);
        curl_setopt($ch, CURLOPT_POSTFIELDS, http_build_query($params));
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
