class MultipartStream
{
    private $boundary;
    private $stream;

    public function __construct(array $elements)
    {
        $this->boundary = sha1(time());
        $this->stream = fopen('php://temp', 'r+');
        foreach ($elements as $element) {
            fwrite($this->stream, "--{$this->boundary}\r\n");
            fwrite($this->stream, "Content-Disposition: form-data; name=\"{$element['name']}\"");
            if (isset($element['filename'])) {
                fwrite($this->stream, "; filename=\"{$element['filename']}\"");
                fwrite($this->stream, "\r\nContent-Type: {$element['type']}");
            }
            fwrite($this->stream, "\r\n\r\n");
            fwrite($this->stream, $element['contents']);
            fwrite($this->stream, "\r\n");
        }
        fwrite($this->stream, "--{$this->boundary}--\r\n");
        rewind($this->stream);
    }

    public function getBoundary(): string
    {
        return $this->boundary;
    }

    public function __toString(): string
    {
        return stream_get_contents($this->stream);
    }
}
