        $this->parts[] = implode("\r\n", $header) . "\r\n\r\n" . $contents . "\r\n";
    }

    public function getBoundary()
    {
        return $this->boundary;
    }

    public function __toString()
    {
        return implode("--{$this->boundary}\r\n", $this->parts) . "--{$this->boundary}--\r\n";
    }
