<?php

class Client
{
    public static function get($url, $headers = [])
    {
        $request = new Request('GET', $url, null, $headers);
        return self::sendRequest($request);
    }

    public static function delete($url, $headers = [])
    {
        $request = new Request('DELETE', $url, null, $headers);
        return self::sendRequest($request);
    }

    public static function post($url, $body, $headers = [])
    {
        $request = new Request('POST', $url, $body, $headers);
        return self::sendRequest($request);
    }

    public static function put($url, $body, $headers = [])
    {
        $request = new Request('PUT', $url, $body, $headers);
        return self::sendRequest($request);
    }

    public static function multipartPost($url, $fields, $name, $fileName, $fileBody, $mimeType = null, $headers = [])
    {
        $multipart = [];
        foreach ($fields as $key => $value) {
            $multipart[] = [
                'name' => $key,
                'contents' => $value
            ];
        }
        $multipart[] = [
            'name' => $name,
            'filename' => $fileName,
            'contents' => $fileBody,
            'type' => $mimeType
        ];
        $body = new MultipartStream($multipart);
        $headers['Content-Type'] = 'multipart/form-data; boundary=' . $body->getBoundary();
        $request = new Request('POST', $url, $body, $headers);
        return self::sendRequest($request);
    }

    private static function sendRequest(Request $request)
    {
        $ch = curl_init();
        curl_setopt($ch, CURLOPT_URL, $request->getUrl());
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($ch, CURLOPT_CUSTOMREQUEST, $request->getMethod());
        if ($request->getBody()) {
            curl_setopt($ch, CURLOPT_POSTFIELDS, (string)$request->getBody());
        }
        $headers = $request->getHeaders();
        $headers[] = 'User-Agent: ' . self::userAgent();
        curl_setopt($ch, CURLOPT_HTTPHEADER, $headers);
        $responseBody = curl_exec($ch);
        $responseCode = curl_getinfo($ch, CURLINFO_HTTP_CODE);
        $responseHeaders = self::parseHeaders(substr($responseBody, 0, curl_getinfo($ch, CURLINFO_HEADER_SIZE)));
        curl_close($ch);
        return new Response($responseCode, $responseHeaders, substr($responseBody, curl_getinfo($ch, CURLINFO_HEADER_SIZE)));
    }

    private static function userAgent()
    {
        return 'PHPClientSDK/' . phpversion() . ' PHP/' . PHP_VERSION . ' (' . PHP_OS . ')';
    }

    private static function parseHeaders($raw)
    {
        $headers = [];
        foreach (explode("\r\n", $raw) as $line) {
            if (strpos($line, ':') !== false) {
                list($key, $value) = explode(':', $line, 2);
                $headers[self::ucwordsHyphen(trim($key))] = trim($value);
            }
        }
        return $headers;
    }

    private static function escapeQuotes($str)
    {
        return str_replace('"', '\"', $str);
    }

    private static function ucwordsHyphen($str)
    {
        return implode('-', array_map('ucfirst', explode('-', $str)));
    }
}

class Request
{
    private $method;
    private $url;
    private $body;
    private $headers;

    public function __construct($method, $url, $body, $headers)
    {
        $this->method = $method;
        $this->url = $url;
        $this->body = $body;
        $this->headers = $headers;
    }

    public function getMethod()
    {
        return $this->method;
    }

    public function getUrl()
    {
        return $this->url;
    }

    public function getBody()
    {
        return $this->body;
    }

    public function getHeaders()
    {
        return $this->headers;
    }
}

class Response
{
    private $statusCode;
    private $headers;
    private $body;

    public function __construct($statusCode, $headers, $body)
    {
        $this->statusCode = $statusCode;
        $this->headers = $headers;
        $this->body = $body;
    }

    public function getStatusCode()
    {
        return $this->statusCode;
    }

    public function getHeaders()
    {
        return $this->headers;
    }

    public function getBody()
    {
        return $this->body;
    }
}

class MultipartStream
{
    private $boundary;
    private $parts = [];

    public function __construct(array $parts)
    {
        $this->boundary = uniqid();
        foreach ($parts as $part) {
            $this->addPart($part['name'], $part['contents'], $part['filename'] ?? null, $part['type'] ?? null);
        }
    }

    private function addPart($name, $contents, $filename = null, $type = null)
    {
        $this->parts[] = [
            'name' => $name,
            'contents' => $contents,
            'filename' => $filename,
            'type' => $type
        ];
    }

    public function getBoundary()
    {
        return $this->boundary;
    }

    public function __toString()
    {
        $stream = '';
        foreach ($this->parts as $part) {
            $stream .= "--{$this->boundary}\r\n";
            $stream .= "Content-Disposition: form-data; name=\"{$part['name']}\"";
            if ($part['filename']) {
                $stream .= "; filename=\"{$part['filename']}\"";
            }
            $stream .= "\r\n";
            if ($part['type']) {
                $stream .= "Content-Type: {$part['type']}\r\n";
            }
            $stream .= "\r\n{$part['contents']}\r\n";
        }
        $stream .= "--{$this->boundary}--\r\n";
        return $stream;
    }
}
