            $headers = @get_headers($url, 1);
            if (!$headers || $headers[0] == 'HTTP/1.1 404 Not Found') {
                return ['error' => "URL not found: $url"];
            }

            if (isset($headers['Content-Encoding']) && $headers['Content-Encoding'] == 'gzip') {
                $saveFile .= '.gz';
            }

            if (function_exists('curl_init') && isset($headers['Accept-Ranges']) && $headers['Accept-Ranges'] == 'bytes') {
                return self::fileDownloadCurl($url, $saveFile, true);
            } else {
                return self::fileDownloadFopen($url, $saveFile);
            }
        }

        return ['error' => 'No valid URLs provided'];
    }

    private static function fileDownloadFopen($url, $fileName, $headerSize = 0) {
        $context = stream_context_create(['http' => ['timeout' => 10]]);
        $file = @fopen($url, 'rb', false, $context);
        if (!$file) {
            return ['error' => "Unable to open URL: $url"];
        }

        $tempFile = fopen($fileName, 'wb');
        if (!$tempFile) {
            fclose($file);
            return ['error' => "Unable to open file for writing: $fileName"];
        }

        while (!feof($file)) {
            $chunk = fread($file, 8192);
            fwrite($tempFile, $chunk);
        }

        fclose($file);
        fclose($tempFile);

        self::checkGzip($fileName);

        return ['success' => true, 'file' => $fileName];
    }

    private static function fileDownloadCurl($url, $fileName, $supportRange = false, $existsLength = 0, $length = 0) {
        $ch = curl_init($url);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);
        curl_setopt($ch, CURLOPT_FOLLOWLOCATION, 1);
        curl_setopt($ch, CURLOPT_TIMEOUT, 10);

        if ($supportRange && $existsLength > 0) {
            curl_setopt($ch, CURLOPT_RANGE, "$existsLength-");
        }

        $data = curl_exec($ch);
        if (curl_errno($ch)) {
            curl_close($ch);
            return ['error' => curl_error($ch)];
        }

        $tempFile = fopen($fileName, $existsLength > 0 ? 'ab' : 'wb');
        if (!$tempFile) {
            curl_close($ch);
            return ['error' => "Unable to open file for writing: $fileName"];
        }

        fwrite($tempFile, $data);
        fclose($tempFile);
        curl_close($ch);

        self::checkGzip($fileName);

        return ['success' => true, 'file' => $fileName];
    }

    private static function checkGzip($file) {
        $fileHandle = fopen($file, 'rb');
        $data = fread($fileHandle, 2);
        fclose($fileHandle);

        if (bin2hex($data) == '1f8b') {
            $gzData = gzfile($file);
            $outFile = substr($file, 0, -3);
            $outHandle = fopen($outFile, 'wb');
            foreach ($gzData as $data) {
                fwrite($outHandle, $data);
            }
            fclose($outHandle);
            unlink($file);
        }
    }
}

