<?php

class Bcs {
    private $rootPath;
    private $error;
    private $config;
    private $bcs;

    public function __construct(array $config) {
        $this->config = $config;
        $this->rootPath = $config['rootPath'] ?? '/';
        $this->error = '';
        $this->bcs = null;
        // Initialize BaiduBCS client
        require_once 'BaiduBCS.class.php';
        $this->bcs = new BaiduBCS($config['AccessKey'], $config['SecretKey'], $config['host']);
    }

    private function request(string $path, string $method, array $headers = null, $body = null) {
        $ch = curl_init();
        $url = $this->config['host'] . $this->rootPath . $path;
        curl_setopt($ch, CURLOPT_URL, $url);
        curl_setopt($ch, CURLOPT_CUSTOMREQUEST, $method);
        if ($headers) {
            curl_setopt($ch, CURLOPT_HTTPHEADER, $headers);
        }
        if ($body !== null) {
            curl_setopt($ch, CURLOPT_POSTFIELDS, $body);
        }
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($ch, CURLOPT_TIMEOUT, $this->config['timeout'] ?? 30);
        $response = curl_exec($ch);
        if (curl_errno($ch)) {
            $this->error = curl_error($ch);
            curl_close($ch);
            return false;
        }
        $httpCode = curl_getinfo($ch, CURLINFO_HTTP_CODE);
        curl_close($ch);
        if ($httpCode >= 400) {
            $this->error = "HTTP Error: $httpCode";
            return false;
        }
        return $this->response($response);
    }

    private function response(string $text) {
        $data = json_decode($text, true);
        if (json_last_error() !== JSON_ERROR_NONE) {
            $this->error = 'Invalid JSON response';
            return false;
        }
        return $data;
    }

    private function error(string $header) {
        $this->error = $header;
    }
}
