class timthumb {
    private $curlFH;
    private $curlDataWritten;
    private $lastURLError;

    public function __construct() {
        $this->curlFH = false;
        $this->curlDataWritten = 0;
        $this->lastURLError = false;
    }

    private static function curlWrite($h, $d) {
        $self = curl_getinfo($h, CURLINFO_PRIVATE);
        $bytesWritten = fwrite($self->curlFH, $d);
        $self->curlDataWritten += $bytesWritten;
        if ($self->curlDataWritten > 10485760) { // 10MB size limit
            return 0;
        }
        return $bytesWritten;
    }

    public function getURL($url, $tempfile) {
        $this->curlFH = fopen($tempfile, 'w');
        if ($this->curlFH === false) {
            $this->lastURLError = "Unable to open temporary file for writing.";
            return false;
        }

        if (function_exists('curl_init')) {
            $ch = curl_init($url);
            curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);
            curl_setopt($ch, CURLOPT_FOLLOWLOCATION, true);
            curl_setopt($ch, CURLOPT_PROGRESSFUNCTION, array($this, 'curlProgress'));
            curl_setopt($ch, CURLOPT_NOPROGRESS, false);
            curl_setopt($ch, CURLOPT_WRITEFUNCTION, array('timthumb', 'curlWrite'));
            curl_setopt($ch, CURLINFO_PRIVATE, $this);
            $data = curl_exec($ch);
            if (curl_errno($ch)) {
                $this->lastURLError = curl_error($ch);
                curl_close($ch);
                fclose($this->curlFH);
                return false;
            }
            $httpStatus = curl_getinfo($ch, CURLINFO_HTTP_CODE);
            if ($httpStatus != 200) {
                $this->lastURLError = "HTTP status code: $httpStatus";
                curl_close($ch);
                fclose($this->curlFH);
                return false;
            }
            curl_close($ch);
        } else {
            $data = file_get_contents($url);
            if ($data === false) {
                $this->lastURLError = "file_get_contents failed to fetch URL.";
                fclose($this->curlFH);
                return false;
            }
            fwrite($this->curlFH, $data);
        }

        fclose($this->curlFH);
        return true;
    }

    private function curlProgress($resource, $download_size, $downloaded, $upload_size, $uploaded) {
        if ($download_size > 0 && $downloaded > 10485760) { // 10MB size limit
            return 1; // Abort the transfer
        }
        return 0;
    }

    public function getLastError() {
        return $this->lastURLError;
    }
}
