<?php
class timthumb {
    private $curlFH;
    private $curlDataWritten;
    private $lastURLError;

    public function curlWrite($h, $d) {
        $len = strlen($d);
        if ($this->curlDataWritten + $len > 10485760) { // 10MB size limit
            return 0;
        }
        $written = fwrite($this->curlFH, $d);
        $this->curlDataWritten += $written;
        return $written;
    }

    public function getURL($url, $tempfile) {
        $this->lastURLError = false;
        $this->curlDataWritten = 0;

        if (function_exists('curl_init')) {
            $ch = curl_init($url);
            $this->curlFH = fopen($tempfile, 'w');
            curl_setopt($ch, CURLOPT_FILE, $this->curlFH);
            curl_setopt($ch, CURLOPT_WRITEFUNCTION, array($this, 'curlWrite'));
            curl_setopt($ch, CURLOPT_FOLLOWLOCATION, true);
            curl_setopt($ch, CURLOPT_MAXREDIRS, 5);
            curl_setopt($ch, CURLOPT_TIMEOUT, 10);
            $result = curl_exec($ch);
            if (curl_errno($ch)) {
                $this->lastURLError = curl_error($ch);
                fclose($this->curlFH);
                curl_close($ch);
                return false;
            }
            $httpStatus = curl_getinfo($ch, CURLINFO_HTTP_CODE);
            if ($httpStatus >= 400) {
                $this->lastURLError = "HTTP error: " . $httpStatus;
                fclose($this->curlFH);
                curl_close($ch);
                return false;
            }
            fclose($this->curlFH);
            curl_close($ch);
            return $result !== false;
        } else {
            $data = file_get_contents($url);
            if ($data === false) {
                $this->lastURLError = "Failed to fetch URL using file_get_contents";
                return false;
            }
            if (file_put_contents($tempfile, $data) === false) {
                $this->lastURLError = "Failed to write data to tempfile";
                return false;
            }
            return true;
        }
    }
}
