class SpotlightClient {
    public function sendRequest(Request $request, string $url): Response {
        if (!extension_loaded('curl')) {
            return new Response(0, null, 'cURL extension is not loaded');
        }

        $curl = curl_init();
        curl_setopt($curl, CURLOPT_URL, $url);
        curl_setopt($curl, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($curl, CURLOPT_HEADER, false);

        if (!empty($request->headers)) {
            curl_setopt($curl, CURLOPT_HTTPHEADER, $request->headers);
        }

        switch ($request->method) {
            case 'POST':
                curl_setopt($curl, CURLOPT_POST, true);
                if (!empty($request->body)) {
                    curl_setopt($curl, CURLOPT_POSTFIELDS, $request->body);
                }
                break;
            case 'PUT':
                curl_setopt($curl, CURLOPT_CUSTOMREQUEST, 'PUT');
                if (!empty($request->body)) {
                    curl_setopt($curl, CURLOPT_POSTFIELDS, $request->body);
                }
                break;
            case 'DELETE':
                curl_setopt($curl, CURLOPT_CUSTOMREQUEST, 'DELETE');
                break;
            default:
                curl_setopt($curl, CURLOPT_HTTPGET, true);
                break;
        }

        $responseBody = curl_exec($curl);
        $statusCode = curl_getinfo($curl, CURLINFO_HTTP_CODE);
        $error = curl_error($curl);

        curl_close($curl);

        return new Response($statusCode, $responseBody, $error ? $error : null);
    }
