    ];
    private $timeout = 0;
    private $stream_method = null;
    private $customUrl = '';
    private $proxy = '';
    private $curlInfo = [];

    public function __construct($OPENAI_API_KEY) {
        $this->headers = [
            'Authorization: Bearer ' . $OPENAI_API_KEY,
            'Content-Type: ' . $this->contentTypes['json']
        ];
    }

    public function sendRequest($url, $method, $opts = []) {
        $ch = curl_init();
        $this->baseUrl($url);
        curl_setopt($ch, CURLOPT_URL, $url);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($ch, CURLOPT_CUSTOMREQUEST, $method);
        curl_setopt($ch, CURLOPT_HTTPHEADER, $this->headers);
        curl_setopt($ch, CURLOPT_TIMEOUT, $this->timeout);

        if (!empty($opts['body'])) {
            curl_setopt($ch, CURLOPT_POSTFIELDS, $opts['body']);
        }

        if ($this->proxy) {
            curl_setopt($ch, CURLOPT_PROXY, $this->proxy);
        }

        if ($this->stream_method) {
            curl_setopt($ch, CURLOPT_WRITEFUNCTION, $this->stream_method);
        }

        $response = curl_exec($ch);
        $this->curlInfo = curl_getinfo($ch);
        curl_close($ch);

        return $response;
    }

    private function baseUrl(&$url) {
        if ($this->customUrl) {
            $parsedUrl = parse_url($url);
            $url = $this->customUrl . (isset($parsedUrl['path']) ? $parsedUrl['path'] : '');
            if (isset($parsedUrl['query'])) {
                $url .= '?' . $parsedUrl['query'];
            }
        }
    }
}

