<?php
function serveFavicon($url, $cache_time, $default_ico, $basename = "favicon.ico") {
    $cache_dir = "cache/";
    $cache_file = $cache_dir . md5($url . $basename) . '.ico';
    $headers = [];

    if (file_exists($cache_file) && (time() - filemtime($cache_file)) < $cache_time) {
        $headers = get_headers($cache_file, 1);
        if (isset($headers['Content-Type']) && strpos($headers['Content-Type'], 'image/') !== false) {
            header("Content-Type: " . $headers['Content-Type']);
            readfile($cache_file);
            return;
        }
    }

    $ch = curl_init();
    curl_setopt($ch, CURLOPT_URL, $url . '/' . $basename);
    curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);
    curl_setopt($ch, CURLOPT_FOLLOWLOCATION, 1);
    curl_setopt($ch, CURLOPT_TIMEOUT, 10);
    $data = curl_exec($ch);
    $http_code = curl_getinfo($ch, CURLINFO_HTTP_CODE);
    $content_type = curl_getinfo($ch, CURLINFO_CONTENT_TYPE);
    curl_close($ch);

    if ($http_code == 200 && strpos($content_type, 'image/') !== false) {
        file_put_contents($cache_file, $data);
        header("Content-Type: " . $content_type);
        echo $data;
    } else {
        header("Content-Type: image/x-icon");
        readfile($default_ico);
    }
}
