    {
        $method = $request->getMethod();
        $uri = $request->getUri()->__toString();
        $headers = "$method $uri HTTP/1.1\r\n";
        foreach ($request->getHeaders() as $name => $values) {
            foreach ($values as $value) {
                $headers .= "$name: $value\r\n";
            }
        }
        $headers .= "Connection: close\r\n\r\n";
        return $headers;
    }

    private function readResponse($socket): Psr\Http\Message\ResponseInterface
    {
        $response = '';
        while (!feof($socket)) {
            $response .= fgets($socket, 128);
            if (strpos($response, "\r\n\r\n") !== false) {
                break;
            }
        }

        list($header, $body) = explode("\r\n\r\n", $response, 2);
        return $this->parseResponse($header, $body);
    }

    private function parseResponse(string $header, string $body): Psr\Http\Message\ResponseInterface
    {
        $lines = explode("\r\n", $header);
        $statusLine = array_shift($lines);
