        $this->socket = socket_create(AF_INET, SOCK_DGRAM, SOL_UDP);
        if ($this->socket === false) {
            throw new Exception("Could not create socket");
        }
        socket_set_option($this->socket, SOL_SOCKET, SO_RCVTIMEO, ['sec' => $this->timeout, 'usec' => 0]);
    }

    private function connect($domain, $port) {
        $this->initSocket();
        if (@socket_connect($this->socket, $domain, $port) === false) {
            throw new Exception("Could not connect to tracker");
        }
    }

    private function generateTransactionId() {
        $this->transactionId = rand(0, 65535);
    }

    private function startTransaction() {
        $this->connect('tracker.domain.com', 80);
        $this->generateTransactionId();
        $this->currentConnectionId = $this->request(pack('N2N', 0x41727101980, 0, $this->transactionId), 16);
    }

    private function scrapeHashes($hashes) {
        $packet = pack('N2N', $this->currentConnectionId, 2, $this->transactionId);
        foreach ($hashes as $hash) {
            $packet .= pack('H*', $hash);
        }
        return $this->request($packet, 2048);
    }

    private function request($packet, $readLength) {
        socket_send($this->socket, $packet, strlen($packet), 0);
        $response = '';
        socket_recv($this->socket, $response, $readLength, 0);
        $this->checkError($response);
        return $response;
    }

    private function closeSocket() {
        if ($this->socket) {
            socket_close($this->socket);
            $this->socket = null;
        }
    }

    private function checkError($response) {
        $data = unpack('Naction/Ntransaction_id', $response);
        if ($data['action'] == 3) {
            throw new Exception("Error from tracker: " . substr($response, 8));
        }
    }
}

