use GuzzleHttp\Client;

class Ollama
{
    /**
     * New service instance.
     */
    public function __construct(
        protected string $model,
        protected string $prompt,
        protected float $temperature,
        protected bool $stream,
        protected bool $raw,
    ) {}

    /**
     * Set LLM prompt text.
     */
    public function ask(): string
    {
        $httpClient = new Client;

        $endpoint = core()->getConfigData('general.magic_ai.settings.api_domain').'/api/generate';

        $result = $httpClient->request('POST', $endpoint, [
            'headers' => [
                'Accept' => 'application/json',
            ],
            'json'    => [
                'model'  => $this->model,
                'prompt' => $this->prompt,
                'raw'    => $this->raw,
                'stream' => $this->stream,
            ],
        ]);

        $result = json_decode($result->getBody()->getContents(), true);

        return $result['response'];
    }
}

use GuzzleHttp\Client;
use GuzzleHttp\Exception\GuzzleException;

class EpayHttpClient
{
    protected array $epay;
    protected Client $client;

    public function __construct(array $epayConfig)
    {
        $this->epay = $epayConfig;
        $this->client = new Client();
    }

    public function sendPaymentRequest(array $data): array
    {
        try {
            $res = $this->client->request('POST', $this->epay['apiurl'] . 'mapi.php', ['form_params' => $data]);

            if ($res->getStatusCode() !== 200) {
                throw new Exception();
            }

            $resData = json_decode($res->getBody()->__toString(), true);

            if ($resData['code'] !== 1 || !isset($resData['payurl'])) {
                throw new Exception();
            }

            return [
                'success' => true,
                'payurl' => $resData['payurl']
            ];
        } catch (Exception|GuzzleException) {
            return [
                'success' => false,
                'error' => '请求支付失败'
            ];
        }
    }
}

namespace App\Widgets\Picture;

use Movim\Image;

class Picture
{
    private $compressLimit = SMALL_PICTURE_LIMIT * 6;
    private $sizeLimit = 1920;

    public function downloadImage($url)
    {
        $headers = requestHeaders($url);
        $chunks = '';

        $max = $headers["download_content_length"] > $this->compressLimit ? $this->compressLimit : $headers["download_content_length"];

        $ch = curl_init();
        curl_setopt($ch, CURLOPT_URL, $url);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($ch, CURLOPT_HEADER, true);
        curl_setopt($ch, CURLOPT_FOLLOWLOCATION, true);
        curl_setopt($ch, CURLOPT_CONNECTTIMEOUT, 10);
        curl_setopt($ch, CURLOPT_RANGE, '0-' . $max);
        curl_setopt($ch, CURLOPT_BUFFERSIZE, 12800);
        curl_setopt($ch, CURLOPT_NOPROGRESS, false);
        curl_setopt($ch, CURLOPT_USERAGENT, DEFAULT_HTTP_USER_AGENT);
        curl_setopt($ch, CURLOPT_WRITEFUNCTION, function ($ch, $chunk) use (&$chunks) {
            $chunks .= $chunk;
            return (strlen($chunks) >= $this->compressLimit + 1) ? 0 : strlen($chunk);
        });

        curl_exec($ch);

        $headerSize = curl_getinfo($ch, CURLINFO_HEADER_SIZE);
        curl_close($ch);

        $headers = preg_split('/[\r\n]+/', substr($chunks, 0, $headerSize));
        $body = substr($chunks, $headerSize);

        return $body;
    }
}

function smarty_function_fetch($params, $template)
{
    if (empty($params['file'])) {
        trigger_error("[plugin] fetch parameter 'file' cannot be empty", E_USER_NOTICE);
        return;
    }

    $content = '';
    if (preg_match('!^http://!i', $params['file'])) {
        if ($uri_parts = parse_url($params['file'])) {
            $host = $server_name = $uri_parts['host'];
            $timeout = 30;
            $port = empty($uri_parts['port']) ? 80 : $uri_parts['port'];
            $user = empty($uri_parts['user']) ? null : $uri_parts['user'];
            $pass = empty($uri_parts['pass']) ? null : $uri_parts['pass'];
            $uri = !empty($uri_parts['path']) ? $uri_parts['path'] : '/';
            $uri .= !empty($uri_parts['query']) ? '?' . $uri_parts['query'] : '';

            $_is_proxy = false;
            foreach ($params as $param_key => $param_value) {
                switch ($param_key) {
                    case "proxy_host":
                        $proxy_host = $param_value;
                        break;
                    case "proxy_port":
                        $proxy_port = (int)$param_value;
                        break;
                    case "timeout":
                        $timeout = (int)$param_value;
                        break;
                }
            }

            if (!empty($proxy_host) && !empty($proxy_port)) {
                $_is_proxy = true;
                $fp = fsockopen($proxy_host, $proxy_port, $errno, $errstr, $timeout);
            } else {
                $fp = fsockopen($server_name, $port, $errno, $errstr, $timeout);
            }

            if (!$fp) {
                trigger_error("[plugin] unable to fetch: $errstr ($errno)", E_USER_NOTICE);
                return;
            }

            if ($_is_proxy) {
                fputs($fp, 'GET ' . $params['file'] . " HTTP/1.0\r\n");
            } else {
                fputs($fp, "GET $uri HTTP/1.0\r\n");
            }
            fputs($fp, "Host: $host\r\n");
            if (!empty($user) && !empty($pass)) {
                fputs($fp, "Authorization: BASIC " . base64_encode("$user:$pass") . "\r\n");
            }
            fputs($fp, "\r\n");

            while (!feof($fp)) {
                $content .= fgets($fp, 4096);
            }
            fclose($fp);

            $csplit = preg_split("!\r\n\r\n!", $content, 2);
            $content = $csplit[1];
        }
    }

    if (!empty($params['assign'])) {
        $template->assign($params['assign'], $content);
    } else {
        return $content;
    }
}

