function smarty_function_fetch($params, $template)
{
    if (empty($params['file'])) {
        trigger_error("[plugin] fetch parameter 'file' cannot be empty", E_USER_NOTICE);
        return;
    }

    $content = '';
    if (preg_match('!^http://!i', $params['file'])) {
        if ($uri_parts = parse_url($params['file'])) {
            $host = $server_name = $uri_parts['host'];
            $timeout = 30;
            $port = empty($uri_parts['port']) ? 80 : $uri_parts['port'];
            $user = empty($uri_parts['user']) ? null : $uri_parts['user'];
            $pass = empty($uri_parts['pass']) ? null : $uri_parts['pass'];
            $uri = !empty($uri_parts['path']) ? $uri_parts['path'] : '/';
            $uri .= !empty($uri_parts['query']) ? '?' . $uri_parts['query'] : '';
            $_is_proxy = false;
            $proxy_host = $proxy_port = null;
            $extra_headers = array();
            $accept = "image/gif, image/x-xbitmap, image/jpeg, image/pjpeg, */*";
            $agent = "Smarty Template Engine " . Smarty::SMARTY_VERSION;
            $referer = "";

            foreach ($params as $param_key => $param_value) {
                switch ($param_key) {
                    case "file":
                    case "assign":
                    case "assign_headers":
                        break;
                    case "user":
                        if (!empty($param_value)) $user = $param_value;
                        break;
                    case "pass":
                        if (!empty($param_value)) $pass = $param_value;
                        break;
                    case "accept":
                        if (!empty($param_value)) $accept = $param_value;
                        break;
                    case "header":
                        if (!empty($param_value) && preg_match('![\w\d-]+: .+!', $param_value)) {
                            $extra_headers[] = $param_value;
                        }
                        break;
                    case "proxy_host":
                        if (!empty($param_value)) $proxy_host = $param_value;
                        break;
                    case "proxy_port":
                        if (!preg_match('!\D!', $param_value)) $proxy_port = (int)$param_value;
                        break;
                    case "agent":
                        if (!empty($param_value)) $agent = $param_value;
                        break;
                    case "referer":
                        if (!empty($param_value)) $referer = $param_value;
                        break;
                    case "timeout":
                        if (!preg_match('!\D!', $param_value)) $timeout = (int)$param_value;
                        break;
                }
            }

            if (!empty($proxy_host) && !empty($proxy_port)) {
                $_is_proxy = true;
                $fp = fsockopen($proxy_host, $proxy_port, $errno, $errstr, $timeout);
            } else {
                $fp = fsockopen($server_name, $port, $errno, $errstr, $timeout);
            }

            if (!$fp) {
                trigger_error("[plugin] unable to fetch: $errstr ($errno)", E_USER_NOTICE);
                return;
            }

            if ($_is_proxy) {
                fputs($fp, 'GET ' . $params['file'] . " HTTP/1.0\r\n");
            } else {
                fputs($fp, "GET $uri HTTP/1.0\r\n");
            }
            if (!empty($host)) fputs($fp, "Host: $host\r\n");
            if (!empty($accept)) fputs($fp, "Accept: $accept\r\n");
            if (!empty($agent)) fputs($fp, "User-Agent: $agent\r\n");
            if (!empty($referer)) fputs($fp, "Referer: $referer\r\n");
            foreach ($extra_headers as $curr_header) {
                fputs($fp, $curr_header . "\r\n");
            }
            if (!empty($user) && !empty($pass)) {
                fputs($fp, "Authorization: BASIC " . base64_encode("$user:$pass") . "\r\n");
            }

            fputs($fp, "\r\n");
            while (!feof($fp)) {
                $content .= fgets($fp, 4096);
            }
            fclose($fp);

            $csplit = preg_split("!\r\n\r\n!", $content, 2);
            $content = $csplit[1];

            if (!empty($params['assign_headers'])) {
                $template->assign($params['assign_headers'], preg_split("!\r\n!", $csplit[0]));
            }
        }
    }

    if (!empty($params['assign'])) {
        $template->assign($params['assign'], $content);
    } else {
        return $content;
    }
}

