            $out .= "Content-Type: " . ($encodetype == 'URLENCODE' ? 'application/x-www-form-urlencoded' : $encodetype) . "\r\n";
            $out .= "Content-Length: " . strlen($post) . "\r\n";
        } else {
            $out .= "GET $path HTTP/1.0\r\n";
        }
        $out .= "Host: $host\r\n";
        $out .= "Cookie: $cookie\r\n";
        $out .= "Connection: Close\r\n\r\n";
        if ($post) {
            $out .= $post;
        }

        $fp = fsockopen(($scheme == 'https' ? 'ssl://' : '') . ($ip ? $ip : $host), $port, $errno, $errstr, $timeout);
        if (!$fp) {
            return '';
        } else {
            stream_set_blocking($fp, $block);
            stream_set_timeout($fp, $timeout);
            fwrite($fp, $out);
            $status = stream_get_meta_data($fp);
            if (!$status['timed_out']) {
                while (!feof($fp)) {
                    if (($header = fgets($fp)) && ($header == "\r\n" || $header == "\n")) {
                        break;
                    }
                }
                while (!feof($fp) && (!$limit || strlen($return) < $limit)) {
                    $data = fread($fp, ($limit ? $limit - strlen($return) : 8192));
                    $return .= $data;
                }
