function _dfsockopen($url, $limit = 0, $post = '', $cookie = '', $bysocket = false, $ip = '', $timeout = 15, $block = true, $encodetype = 'URLENCODE', $allowcurl = true, $position = 0, $files = array()) {
    $return = '';
    $matches = parse_url($url);
    $scheme = $matches['scheme'];
    $host = $matches['host'];
    $path = !empty($matches['path']) ? $matches['path'] . (!empty($matches['query']) ? '?' . $matches['query'] : '') : '/';
    $port = !empty($matches['port']) ? $matches['port'] : ($scheme == 'https' ? 443 : 80);
    $boundary = '----WebKitFormBoundary' . substr(md5(rand(0, 32000)), 0, 10);

    if ($post) {
        if (!is_array($post)) {
            parse_str($post, $post);
        }
        if ($encodetype == 'URLENCODE') {
            $postdata = http_build_query($post);
        } else {
            $postdata = '';
            foreach ($post as $key => $val) {
                $postdata .= "--$boundary\r\n";
                $postdata .= "Content-Disposition: form-data; name=\"$key\"\r\n\r\n$val\r\n";
            }
            foreach ($files as $key => $file) {
                $postdata .= "--$boundary\r\n";
                $postdata .= "Content-Disposition: form-data; name=\"$key\"; filename=\"" . basename($file) . "\"\r\n";
                $postdata .= "Content-Type: application/octet-stream\r\n\r\n";
                $postdata .= file_get_contents($file) . "\r\n";
            }
            $postdata .= "--$boundary--\r\n";
        }
    }

    if (function_exists('curl_init') && function_exists('curl_exec') && $allowcurl) {
        $ch = curl_init();
        $httpheader = array();
        if ($ip) {
            $httpheader[] = "Host: " . $host;
            $url = $scheme . '://' . $ip . ($port == 80 ? '' : ':' . $port) . $path;
        }
        curl_setopt($ch, CURLOPT_URL, $url);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);
        curl_setopt($ch, CURLOPT_CONNECTTIMEOUT, $timeout);
        if ($post) {
            curl_setopt($ch, CURLOPT_POST, 1);
            curl_setopt($ch, CURLOPT_POSTFIELDS, $postdata);
            if ($encodetype != 'URLENCODE') {
                $httpheader[] = 'Content-Type: multipart/form-data; boundary=' . $boundary;
            }
        }
        if ($httpheader) {
            curl_setopt($ch, CURLOPT_HTTPHEADER, $httpheader);
        }
        if ($cookie) {
            curl_setopt($ch, CURLOPT_COOKIE, $cookie);
        }
        $data = curl_exec($ch);
        $status = curl_getinfo($ch);
        $errno = curl_errno($ch);
        curl_close($ch);
        if ($errno || $status['http_code'] != 200) {
            return '';
        } else {
            return !$limit ? $data : substr($data, 0, $limit);
        }
    }

    if ($post) {
        $out = "POST $path HTTP/1.0\r\n";
        $header = "Content-Type: " . ($encodetype == 'URLENCODE' ? "application/x-www-form-urlencoded" : "multipart/form-data; boundary=$boundary") . "\r\n";
        $header .= "Content-Length: " . strlen($postdata) . "\r\n";
    } else {
        $out = "GET $path HTTP/1.0\r\n";
        $header = '';
    }
    $header .= "Host: $host\r\n";
    $header .= "Connection: Close\r\n";
    if ($cookie) {
        $header .= "Cookie: $cookie\r\n";
    }
    $out .= $header . "\r\n" . ($post ? $postdata : '');

    $fp = @fsockopen(($scheme == 'https' ? 'ssl://' : '') . ($ip ? $ip : $host), $port, $errno, $errstr, $timeout);
    if (!$fp) {
        return '';
    } else {
        stream_set_blocking($fp, $block);
        stream_set_timeout($fp, $timeout);
        fwrite($fp, $out);
        $status = stream_get_meta_data($fp);
        if (!$status['timed_out']) {
            while (!feof($fp) && !$status['timed_out']) {
                $header = fgets($fp);
                if ($header && ($header == "\r\n" || $header == "\n")) {
                    break;
                }
                $GLOBALS['filesockheader'] .= $header;
            }
            while (!feof($fp) && !$status['timed_out']) {
                $data = fread($fp, $limit == 0 || $limit > 8192 ? 8192 : $limit);
                $return .= $data;
                if ($limit) {
                    $limit -= strlen($data);
                    if ($limit <= 0) {
                        break;
                    }
                }
                $status = stream_get_meta_data($fp);
            }
        }
        fclose($fp);
        return $return;
    }
}

