    public function write($session_id, $session_data) {
        if ($this->_lock_key === $this->_key_prefix.$session_id.':lock') {
            $this->_redis->set($this->_key_prefix.$session_id, $session_data);
            $this->_redis->expire($this->_key_prefix.$session_id, $this->_config['expiration']);
            $this->_fingerprint = md5($session_data);
            return $this->_success();
        }
        return $this->_fail();
    }

    public function close() {
        if (isset($this->_lock_key)) {
            $this->_release_lock();
        }
        return $this->_redis->close();
    }

    public function destroy($session_id) {
        if ($this->_lock_key === $this->_key_prefix.$session_id.':lock') {
            $this->_redis->del($this->_key_prefix.$session_id);
            $this->_release_lock();
            return $this->_success();
        }
        return $this->_fail();
    }

    public function gc($maxlifetime) {
        return TRUE;
    }

    protected function _get_lock($session_id) {
        $lock_key = $this->_key_prefix.$session_id.':lock';
        $attempt = 0;
        do {
            if ($this->_redis->set($lock_key, time(), array('nx', 'ex' => 300))) {
                $this->_lock_key = $lock_key;
                break;
            }
            usleep(100000);
        } while (++$attempt < 30);
        if ($attempt === 30) {
            return $this->_fail();
        }
        return $this->_success();
    }

    protected function _release_lock() {
        if ($this->_lock_key) {
            $this->_redis->del($this->_lock_key);
            $this->_lock_key = NULL;
        }
        return $this->_success();
    }

    protected function _fail() {
        return FALSE;
    }

    protected function _success() {
        return TRUE;
    }
