class Pretreat
{
    private $config;
    private $client;

    public function __construct(Config $config)
    {
        if (empty($config->processNotifyUrl)) {
            throw new Exception("processNotifyUrl must be configured");
        }
        $this->config = $config;
        $this->client = new \GuzzleHttp\Client([
            'timeout' => $config->timeout,
            'base_uri' => $config->serviceEndpoint
        ]);
    }

    public function process(array $tasks, array $optionalParams = []): array
    {
        $params = array_merge(['tasks' => $tasks], $optionalParams);
        $response = $this->client->post($this->config->processPath, [
            'json' => $params,
            'headers' => $this->getHeaders($params)
        ]);

        return json_decode($response->getBody()->getContents(), true);
    }

    public function query(array $taskIds, string $path): array|bool
    {
        $params = ['taskIds' => $taskIds];
        try {
            $response = $this->client->get($path, [
                'query' => $params,
                'headers' => $this->getHeaders($params)
            ]);
            return json_decode($response->getBody()->getContents(), true);
        } catch (\GuzzleHttp\Exception\RequestException $e) {
            return false;
        }
    }

    private function getHeaders(array $params): array
    {
        $signature = $this->signRequest($params);
        return [
            'Authorization' => 'Bearer ' . $this->config->apiKey,
            'X-Signature' => $signature
        ];
    }

    private function signRequest(array $params): string
    {
        ksort($params);
        $queryString = http_build_query($params);
        return hash_hmac('sha256', $queryString, $this->config->secretKey);
    }
}

