namespace App\Payments;

class BTCPay {
    private $config;

    public function __construct($config) {
        $this->config = $config;
    }

    public function pay($order) {
        $params = [
            'jsonResponse' => true,
            'amount' => sprintf('%.2f', $order['total_amount'] / 100),
            'currency' => 'CNY',
            'metadata' => [
                'orderId' => $order['trade_no']
            ]
        ];

        $params_string = @json_encode($params);
        $ret_raw = $this->_curlPost($this->config['btcpay_url'] . 'api/v1/stores/' . $this->config['btcpay_storeId'] . '/invoices', $params_string);
        $ret = @json_decode($ret_raw, true);

        if(empty($ret['checkoutLink'])) {
            abort(500, "error!");
        }
        return [
            'type' => 1,
            'data' => $ret['checkoutLink'],
        ];
    }

    private function _curlPost($url, $params=false) {
        $ch = curl_init();
        curl_setopt($ch, CURLOPT_URL, $url);
        curl_setopt($ch, CURLOPT_HEADER, 0);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);
        curl_setopt($ch, CURLOPT_TIMEOUT, 300);
        curl_setopt($ch, CURLOPT_POSTFIELDS, $params);
        curl_setopt(
            $ch, 
            CURLOPT_HTTPHEADER, 
            array(
                'Authorization:' .'token '.$this->config['btcpay_api_key'], 
                'Content-Type: application/json'
