        $responseHeaders = self::parseHeaders(substr($responseBody, 0, curl_getinfo($ch, CURLINFO_HEADER_SIZE)));
        $responseBody = substr($responseBody, curl_getinfo($ch, CURLINFO_HEADER_SIZE));

