use GuzzleHttp\Client;
use GuzzleHttp\Exception\RequestException;
use GuzzleHttp\Psr7\Message;

class WebhookService
{
    protected Client $client;

    public function __construct()
    {
        $this->client = new Client;
    }

    public function triggerWebhook(mixed $data): array
    {
        $options = [
            'headers'     => $this->formatHeaders(json_decode($data['headers'], true)),
            'form_params' => $this->formatPayload(json_decode($data['payload'], true)),
        ];

        try {
            $response = $this->client->request(
                $data['method'],
                $data['end_point'],
                $options,
            );

            return [
                'status'   => 'success',
                'response' => $response->getBody()->getContents(),
            ];
        } catch (RequestException $e) {
            return [
                'status'   => 'error',
                'response' => $e->hasResponse() ? Message::toString($e->getResponse()) : $e->getMessage(),
            ];
        }
    }

    protected function formatHeaders(array $headers): array
    {
        $formattedHeaders = [];

        foreach ($headers as $header) {
            $formattedHeaders[$header['key']] = $header['value'];
        }

        return $formattedHeaders;
    }

    private function formatPayload($payload): array|string
    {
        if (! is_array($payload)) {
            $payload = json_decode($payload, true);
        }

        $formattedPayload = [];

        if (
            isset($payload['key'])
            && isset($payload['value'])
        ) {
            $formattedPayload[$payload['key']] = $payload['value'];
        } else {
            foreach ($payload as $item) {
                $formattedPayload[$item['key']] = $item['value'];
            }
        }

        return $formattedPayload;
    }
}

function pk_get_website_favicon_ico($url, $cache_time, $default_ico, $basename = "favicon.ico")
{
    $cache_filename = 'icon-'.md5($url) . '.ico';
    $cache_file = dirname(__FILE__) . '/../cache/' . $cache_filename;
    if (is_file($cache_file)) {
        if (time() - filemtime($cache_file) <= $cache_time) {
            pk_favicon_get_ico_contents($cache_file, $cache_filename);
            return;
        }
    }
    $ch = curl_init($url . '/' . $basename);
    $ico_file = fopen($cache_file, 'w');
    curl_setopt($ch, CURLOPT_CONNECTTIMEOUT, 2);
    curl_setopt($ch, CURLOPT_SSL_VERIFYPEER, false);
    curl_setopt($ch, CURLOPT_SSL_VERIFYHOST, false);
    curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
    curl_setopt($ch, CURLOPT_FAILONERROR, true);
    curl_setopt($ch, CURLOPT_FOLLOWLOCATION, true);
    curl_setopt($ch, CURLOPT_FILE, $ico_file);
    curl_exec($ch);
    fclose($ico_file);
    if (curl_getinfo($ch, CURLINFO_HTTP_CODE) != 200) {
        curl_close($ch);
        @unlink($cache_file);
        pk_favicon_put_default_and_output($cache_file, $cache_filename, $default_ico);
        return;
    }
    $mimeArray = explode('/', curl_getinfo($ch, CURLINFO_CONTENT_TYPE));
    curl_close($ch);
    if (count($mimeArray) == 0 || $mimeArray[0] != 'image') {
        @unlink($cache_file);
        pk_favicon_put_default_and_output($cache_file, $cache_filename, $default_ico);
        return;
    }
    pk_favicon_get_ico_contents($cache_file, $cache_filename);
}

