        }

        return $this->response;
    }

    private function isBaeEnv()
    {
        if (isset($_SERVER['HTTP_HOST'])) {
            $host = $_SERVER['HTTP_HOST'];
            $pos = strpos($host, '.');
            if ($pos !== false) {
                $substr = substr($host, $pos + 1);
                if ($substr == 'duapp.com') {
                    return true;
