    curl_setopt($ch, CURLOPT_FOLLOWLOCATION, 1);
    curl_setopt($ch, CURLOPT_PROTOCOLS, CURLPROTO_HTTP | CURLPROTO_HTTPS);
    curl_setopt($ch, CURLINFO_HEADER_OUT, 1);
    curl_setopt($ch, CURLOPT_SSL_VERIFYPEER, 0);
    curl_setopt($ch, CURLOPT_SSL_VERIFYHOST, 0);
    curl_setopt($ch, CURLOPT_TIMEOUT, $timeout);
    curl_setopt($ch, CURLOPT_REFERER, get_url_link($url));
    curl_setopt($ch, CURLOPT_USERAGENT, 'Mozilla/5.0 (Windows NT 6.2; WOW64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/27.0.1453.94 Safari/537.36');

    if ($headers) {
        if (is_string($headers)) {
            $headers = array($headers);
        }
        curl_setopt($ch, CURLOPT_HTTPHEADER, $headers);
    }

    switch ($method) {
        case 'GET':
            curl_setopt($ch, CURLOPT_HTTPGET, 1);
            break;
        case 'POST':
            curl_setopt($ch, CURLOPT_POST, 1);
            curl_setopt($ch, CURLOPT_POSTFIELDS, $data);
            break;
        case 'PUT':
        case 'DELETE':
        case 'PATCH':
        case 'OPTIONS':
            curl_setopt($ch, CURLOPT_CUSTOMREQUEST, $method);
            curl_setopt($ch, CURLOPT_POSTFIELDS, $data);
            break;
    }

    if (!empty($options)) {
        curl_setopt_array($ch, $options);
    }

    $response = curl_exec($ch);
    $header_size = curl_getinfo($ch, CURLINFO_HEADER_SIZE);
    $response_info = curl_getinfo($ch);
    $http_body = substr($response, $header_size);
    $http_header = substr($response, 0, $header_size);
    $http_header = parse_headers($http_header);

    if ($response_info['http_code'] == 0) {
        $error_message = curl_error($ch);
        if (!empty($error_message)) {
            $error_message = "API call to $url failed; $error_message";
        } else {
            $error_message = "API call to $url failed; maybe network error!";
        }
        return array(
            'data' => $error_message,
            'code' => 0,
            'header' => $response_info,
        );
    }

    curl_close($ch);

    if ($json && ($response_info['http_code'] >= 200 && $response_info['http_code'] <= 299)) {
        $data = @json_decode($http_body, true);
        if (json_last_error() == 0) {
            $http_body = $data;
        }
    }

    return array(
        'data' => $http_body,
        'status' => ($response_info['http_code'] >= 200 && $response_info['http_code'] <= 299),
        'code' => $response_info['http_code'],
        'header' => $http_header,
    );
}

use Monolog\Logger;

class ProcessHandler extends AbstractProcessingHandler
{
    private $process;
    private $command;
    private $cwd;
    private $pipes = [];
    protected const DESCRIPTOR_SPEC = [
        0 => ['pipe', 'r'],
        1 => ['pipe', 'w'],
        2 => ['pipe', 'w'],
    ];

    public function __construct(string $command, $level = Logger::DEBUG, bool $bubble = true, ?string $cwd = null)
    {
        if ($command === '') {
            throw new \InvalidArgumentException('The command argument must be a non-empty string.');
        }
        if ($cwd === '') {
            throw new \InvalidArgumentException('The optional CWD argument must be a non-empty string or null.');
        }

        parent::__construct($level, $bubble);

        $this->command = $command;
        $this->cwd = $cwd;
    }

    protected function write(array $record): void
    {
        $this->ensureProcessIsStarted();
        $this->writeProcessInput($record['formatted']);
        $errors = $this->readProcessErrors();
        if (empty($errors) === false) {
            throw new \UnexpectedValueException(sprintf('Errors while writing to process: %s', $errors));
        }
    }

    private function ensureProcessIsStarted(): void
    {
        if (is_resource($this->process) === false) {
            $this->startProcess();
            $this->handleStartupErrors();
        }
    }

    private function startProcess(): void
    {
        $this->process = proc_open($this->command, static::DESCRIPTOR_SPEC, $this->pipes, $this->cwd);
        foreach ($this->pipes as $pipe) {
            stream_set_blocking($pipe, false);
        }
    }

    private function handleStartupErrors(): void
    {
        $selected = $this->selectErrorStream();
        if (false === $selected) {
            throw new \UnexpectedValueException('Something went wrong while selecting a stream.');
        }

        $errors = $this->readProcessErrors();
        if (is_resource($this->process) === false || empty($errors) === false) {
            throw new \UnexpectedValueException(
                sprintf('The process "%s" could not be opened: ' . $errors, $this->command)
            );
        }
    }

    protected function selectErrorStream()
    {
        $empty = [];
        $errorPipes = [$this->pipes[2]];
        return stream_select($errorPipes, $empty, $empty, 1);
    }

    protected function readProcessErrors(): string
    {
        return (string) stream_get_contents($this->pipes[2]);
    }

    protected function writeProcessInput(string $string): void
    {
        fwrite($this->pipes[0], $string);
    }

    public function close(): void
    {
        if (is_resource($this->process)) {
            foreach ($this->pipes as $pipe) {
                fclose($pipe);
            }
            proc_close($this->process);
            $this->process = null;
        }
    }
}

use GuzzleHttp\Client;
use GuzzleHttp\Exception\GuzzleException;

class HttpClient
{
    private Client $client;

    public function __construct()
    {
        $this->client = new Client();
    }

    /**
     * Download remote file via HTTP
     *
     * @param string $fileName
     * @param string $savePath
     * @param string $url
     * @return bool
     */
    public function getSourceFile(string $fileName, string $savePath, string $url): bool
    {
        try {
            if (! file_exists($savePath)) {
                echo '目标文件夹 ' . $savePath . ' 不存在，下載失败。' . PHP_EOL;
                return false;
            }

            echo '- 开始下载 ' . $fileName . '...' . PHP_EOL;
            $request = $this->client->get($url);
            echo '- 下载 ' . $fileName . ' 成功，正在保存...' . PHP_EOL;
            $result = file_put_contents($savePath . $fileName, $request->getBody()->getContents());

            if (! $result) {
                echo '- 保存 ' . $fileName . ' 至 ' . $savePath . ' 失败。' . PHP_EOL;
            } else {
                echo '- 保存 ' . $fileName . ' 至 ' . $savePath . ' 成功。' . PHP_EOL;
            }

            return true;
        } catch (GuzzleException $e) {
            echo '- 下载 ' . $fileName . ' 失败...' . PHP_EOL;
            echo $e->getMessage() . PHP_EOL;

            return false;
        }
    }

    /**
     * Get latest GitHub release tag
     *
     * @param string $repo
     * @return string
     * @throws GuzzleException
     */
    public function getLatestReleaseTagName(string $repo): string
    {
        $url = 'https://api.github.com/repos/' . $repo . '/releases/latest' .
            ($_ENV['github_access_token'] !== '' ? '?access_token=' . $_ENV['github_access_token'] : '');
        $request = $this->client->get($url);

        return json_decode(
            $request->getBody()->getContents(),
            true
        )['tag_name'];
    }

    /**
     * Get latest GitHub pre-release tag
     *
     * @param string $repo
     * @return string
     * @throws GuzzleException
     */
    public function getLatestPreReleaseTagName(string $repo): string
    {
        $url = 'https://api.github.com/repos/' . $repo . '/releases' .
            ($_ENV['github_access_token'] !== '' ? '?access_token=' . $_ENV['github_access_token'] : '');
        $request = $this->client->get($url);
        $latest = json_decode(
            $request->getBody()->getContents(),
            true
        )[0];

        return $latest['tag_name'];
    }
}

namespace Think\Upload\Driver\Qiniu;

class QiniuStorage
{
    public $QINIU_RSF_HOST = 'http://rsf.qbox.me';
    public $QINIU_RS_HOST = 'http://rs.qbox.me';
    public $QINIU_UP_HOST = 'http://up.qiniu.com';
    public $timeout = '';

    private function request($path, $method, $headers = null, $body = null)
    {
        $ch = curl_init($path);

        $_headers = array('Expect:');
        if (!is_null($headers) && is_array($headers)) {
            foreach ($headers as $k => $v) {
                array_push($_headers, "{$k}: {$v}");
            }
        }

        $length = 0;
        $date = gmdate('D, d M Y H:i:s \G\M\T');

        if (!is_null($body)) {
            if (is_resource($body)) {
                fseek($body, 0, SEEK_END);
                $length = ftell($body);
                fseek($body, 0);

                array_push($_headers, "Content-Length: {$length}");
                curl_setopt($ch, CURLOPT_INFILE, $body);
                curl_setopt($ch, CURLOPT_INFILESIZE, $length);
            } else {
                $length = @strlen($body);
                array_push($_headers, "Content-Length: {$length}");
                curl_setopt($ch, CURLOPT_POSTFIELDS, $body);
            }
        } else {
            array_push($_headers, "Content-Length: {$length}");
        }

        array_push($_headers, "Date: {$date}");

        curl_setopt($ch, CURLOPT_HTTPHEADER, $_headers);
        curl_setopt($ch, CURLOPT_TIMEOUT, $this->timeout);
        curl_setopt($ch, CURLOPT_HEADER, 1);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);
        curl_setopt($ch, CURLOPT_FOLLOWLOCATION, 0);
        curl_setopt($ch, CURLOPT_CUSTOMREQUEST, $method);

        if ('PUT' == $method || 'POST' == $method) {
            curl_setopt($ch, CURLOPT_POST, 1);
        } else {
            curl_setopt($ch, CURLOPT_POST, 0);
        }

        if ('HEAD' == $method) {
            curl_setopt($ch, CURLOPT_NOBODY, true);
        }

        $response = curl_exec($ch);
        $status = curl_getinfo($ch, CURLINFO_HTTP_CODE);
        curl_close($ch);
        list($header, $body) = explode("\r\n\r\n", $response, 2);
        if (200 == $status) {
            if ('GET' == $method) {
                return $body;
            } else {
                return $this->response($response);
            }
        } else {
            $this->error($header, $body);
            return false;
        }
    }

    private function response($text)
    {
        $headers = explode(PHP_EOL, $text);
        $items = array();
        foreach ($headers as $header) {
            $header = trim($header);
            if (strpos($header, '{') !== false) {
                $items = json_decode($header, 1);
                break;
            }
        }
        return $items;
    }

    private function error($header, $body)
    {
        list($status, $stash) = explode("\r\n", $header, 2);
        list($v, $code, $message) = explode(" ", $status, 3);
        $message = is_null($message) ? 'File Not Found' : "[{$status}]:{$message}]";
        $this->error = $message;
        $this->errorStr = json_decode($body, 1);
        $this->errorStr = $this->errorStr['error'];
    }
}

class RequestCore
{
    public $request_url;
    public $request_headers;
    public $request_body;
    public $response;
    public $response_headers;
    public $response_body;
    public $response_code;
    public $response_info;
    public $curl_handle;
    public $method;
    public $proxy = null;
    public $username = null;
    public $password = null;
    public $curlopts = null;
    public $debug_mode = false;
    public $request_class = 'RequestCore';
    public $response_class = 'ResponseCore';
    public $useragent = 'RequestCore/1.4.3';
    public $read_file = null;
    public $read_stream = null;
    public $read_stream_size = null;
    public $read_stream_read = 0;
    public $write_file = null;
    public $write_stream = null;
    public $seek_position = null;
    public $cacert_location = false;
    public $ssl_verification = true;
    public $registered_streaming_read_callback = null;
    public $registered_streaming_write_callback = null;

    const HTTP_GET = 'GET';
    const HTTP_POST = 'POST';
    const HTTP_PUT = 'PUT';
    const HTTP_DELETE = 'DELETE';
    const HTTP_HEAD = 'HEAD';

    public function __construct($url = null, $proxy = null, $helpers = null)
    {
        $this->request_url = $url;
        $this->method = self::HTTP_GET;
        $this->request_headers = array();
        $this->request_body = '';

        if (isset($helpers['request']) && !empty($helpers['request']))
        {
            $this->request_class = $helpers['request'];
        }

        if (isset($helpers['response']) && !empty($helpers['response']))
        {
            $this->response_class = $helpers['response'];
        }

        if ($proxy)
        {
            $this->set_proxy($proxy);
        }

        return $this;
    }

    public function __destruct()
    {
        if (isset($this->read_file) && isset($this->read_stream))
        {
            fclose($this->read_stream);
        }

        if (isset($this->write_file) && isset($this->write_stream))
        {
            fclose($this->write_stream);
        }

        return $this;
    }

    public function set_credentials($user, $pass)
    {
        $this->username = $user;
        $this->password = $pass;
        return $this;
    }

    public function add_header($key, $value)
    {
        $this->request_headers[$key] = $value;
        return $this;
    }

    public function remove_header($key)
    {
        if (isset($this->request_headers[$key]))
        {
            unset($this->request_headers[$key]);
        }
        return $this;
    }

    public function set_method($method)
    {
        $this->method = strtoupper($method);
        return $this;
    }

    public function set_useragent($ua)
    {
        $this->useragent = $ua;
        return $this;
    }

    public function set_body($body)
    {
        $this->request_body = $body;
        return $this;
    }

    public function set_request_url($url)
    {
        $this->request_url = $url;
        return $this;
    }

    public function set_curlopts($curlopts)
    {
        $this->curlopts = $curlopts;
        return $this;
    }

    public function set_read_stream_size($size)
    {
        $this->read_stream_size = $size;
        return $this;
    }

    public function set_read_stream($resource, $size = null)
    {
        if (!isset($size) || $size < 0)
        {
            $stats = fstat($resource);

            if ($stats && $stats['size'] >= 0)
            {
                $position = ftell($resource);

                if ($position !== false && $position >= 0)
                {
                    $size = $stats['size'] - $position;
                }
            }
        }

        $this->read_stream = $resource;
        return $this->set_read_stream_size($size);
    }

    public function set_read_file($location)
    {
        $this->read_file = $location;
        $read_file_handle = fopen($location, 'r');
        return $this->set_read_stream($read_file_handle);
    }

    public function set_write_stream($resource)
    {
        $this->write_stream = $resource;
        return $this;
    }

    public function set_write_file($location)
    {
        $this->write_file = $location;
        $write_file_handle = fopen($location, 'w');
        return $this->set_write_stream($write_file_handle);
    }

    public function set_proxy($proxy)
    {
        $proxy = parse_url($proxy);
        $proxy['user'] = isset($proxy['user']) ? $proxy['user'] : null;
        $proxy['pass'] = isset($proxy['pass']) ? $proxy['pass'] : null;
        $proxy['port'] = isset($proxy['port']) ? $proxy['port'] : null;
        $this->proxy = $proxy;
        return $this;
    }

    public function set_seek_position($position)
    {
        $this->seek_position = isset($position) ? (integer) $position : null;
        return $this;
    }

    public function register_streaming_read_callback($callback)
    {
        $this->registered_streaming_read_callback = $callback;
        return $this;
    }

    public function register_streaming_write_callback($callback)
    {
        $this->registered_streaming_write_callback = $callback;
        return $this;
    }

    public function streaming_read_callback($curl_handle, $file_handle, $length)
    {
        if ($this->read_stream_read >= $this->read_stream_size)
        {
            return '';
        }

        if ($this->read_stream_read == 0 && isset($this->seek_position) && $this->seek_position !== ftell($this->read_stream))
        {
            if (fseek($this->read_stream, $this->seek_position) !== 0)
            {
                throw new RequestCore_Exception('The stream does not support seeking and is either not at the requested position or the position is unknown.');
            }
        }

        $read = fread($this->read_stream, min($this->read_stream_size - $this->read_stream_read, $length));
        $this->read_stream_read += strlen($read);

        $out = $read === false ? '' : $read;

        if ($this->registered_streaming_read_callback)
        {
            call_user_func($this->registered_streaming_read_callback, $curl_handle, $file_handle, $out);
        }

        return $out;
    }

    public function streaming_write_callback($curl_handle, $data)
    {
        $length = strlen($data);
        $written_total = 0;
        $written_last = 0;

        while ($written_total < $length)
        {
            $written_last = fwrite($this->write_stream, substr($data, $written_total));

            if ($written_last === false)
            {
                return $written_total;
            }

            $written_total += $written_last;
        }

        if ($this->registered_streaming_write_callback)
        {
            call_user_func($this->registered_streaming_write_callback, $curl_handle, $written_total);
        }

        return $written_total;
    }

    public function prep_request()
    {
        $curl_handle = curl_init();

        curl_setopt($curl_handle, CURLOPT_URL, $this->request_url);
        curl_setopt($curl_handle, CURLOPT_FILETIME, true);
        curl_setopt($curl_handle, CURLOPT_FRESH_CONNECT, false);
        curl_setopt($curl_handle, CURLOPT_CLOSEPOLICY, CURLCLOSEPOLICY_LEAST_RECENTLY_USED);
        curl_setopt($curl_handle, CURLOPT_MAXREDIRS, 5);
        curl_setopt($curl_handle, CURLOPT_HEADER, true);
        curl_setopt($curl_handle, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($curl_handle, CURLOPT_TIMEOUT, 5184000);
        curl_setopt($curl_handle, CURLOPT_CONNECTTIMEOUT, 120);
        curl_setopt($curl_handle, CURLOPT_NOSIGNAL, true);
        curl_setopt($curl_handle, CURLOPT_REFERER, $this->request_url);
        curl_setopt($curl_handle, CURLOPT_USERAGENT, $this->useragent);
        curl_setopt($curl_handle, CURLOPT_READFUNCTION, array($this, 'streaming_read_callback'));

        if ($this->ssl_verification)
        {
            curl_setopt($curl_handle, CURLOPT_SSL_VERIFYPEER, true);
            curl_setopt($curl_handle, CURLOPT_SSL_VERIFYHOST, true);
        }
        else
        {
            curl_setopt($curl_handle, CURLOPT_SSL_VERIFYPEER, false);
            curl_setopt($curl_handle, CURLOPT_SSL_VERIFYHOST, false);
        }

        if ($this->cacert_location === true)
        {
            curl_setopt($curl_handle, CURLOPT_CAINFO, dirname(__FILE__) . '/cacert.pem');
        }
        elseif (is_string($this->cacert_location))
        {
            curl_setopt($curl_handle, CURLOPT_CAINFO, $this->cacert_location);
        }

        if ($this->debug_mode)
        {
            curl_setopt($curl_handle, CURLOPT_VERBOSE, true);
        }

        if (!ini_get('safe_mode') && !ini_get('open_basedir'))
        {
            curl_setopt($curl_handle, CURLOPT_FOLLOWLOCATION, true);
        }

        if ($this->proxy)
        {
            curl_setopt($curl_handle, CURLOPT_HTTPPROXYTUNNEL, true);

            $host = $this->proxy['host'];
            $host .= ($this->proxy['port']) ? ':' . $this->proxy['port'] : '';
            curl_setopt($curl_handle, CURLOPT_PROXY, $host);

            if (isset($this->proxy['user']) && isset($this->proxy['pass']))
            {
                curl_setopt($curl_handle, CURLOPT_PROXYUSERPWD, $this->proxy['user'] . ':' . $this->proxy['pass']);
            }
        }

        if ($this->username && $this->password)
        {
            curl_setopt($curl_handle, CURLOPT_HTTPAUTH, CURLAUTH_ANY);
            curl_setopt($curl_handle, CURLOPT_USERPWD, $this->username . ':' . $this->password);
        }

        if (extension_loaded('zlib'))
        {
            curl_setopt($curl_handle, CURLOPT_ENCODING, '');
        }

        if (isset($this->request_headers) && count($this->request_headers))
        {
            $temp_headers = array();

            foreach ($this->request_headers as $k => $v)
            {
                $temp_headers[] = $k . ': ' . $v;
            }

            curl_setopt($curl_handle, CURLOPT_HTTPHEADER, $temp_headers);
        }

        switch ($this->method)
        {
            case self::HTTP_PUT:
                curl_setopt($curl_handle, CURLOPT_CUSTOMREQUEST, 'PUT');
                if (isset($this->read_stream))
                {
                    if (!isset($this->read_stream_size) || $this->read_stream_size < 0)
                    {
                        throw new RequestCore_Exception('The stream size for the streaming upload cannot be determined.');
                    }

                    curl_setopt($curl_handle, CURLOPT_INFILESIZE, $this->read_stream_size);
                    curl_setopt($curl_handle, CURLOPT_UPLOAD, true);
                }
                else
                {
                    curl_setopt($curl_handle, CURLOPT_POSTFIELDS, $this->request_body);
                }
                break;

            case self::HTTP_POST:
                curl_setopt($curl_handle, CURLOPT_POST, true);
                curl_setopt($curl_handle, CURLOPT_POSTFIELDS, $this->request_body);
                break;

            case self::HTTP_HEAD:
                curl_setopt($curl_handle, CURLOPT_CUSTOMREQUEST, self::HTTP_HEAD);
                curl_setopt($curl_handle, CURLOPT_NOBODY, 1);
                break;

            default:
                curl_setopt($curl_handle, CURLOPT_CUSTOMREQUEST, $this->method);
                if (isset($this->write_stream))
                {
                    curl_setopt($curl_handle, CURLOPT_WRITEFUNCTION, array($this, 'streaming_write_callback'));
                    curl_setopt($curl_handle, CURLOPT_HEADER, false);
                }
                else
                {
                    curl_setopt($curl_handle, CURLOPT_POSTFIELDS, $this->request_body);
                }
                break;
        }

        if (isset($this->curlopts) && sizeof($this->curlopts) > 0)
        {
            foreach ($this->curlopts as $k => $v)
            {
                curl_setopt($curl_handle, $k, $v);
            }
        }

        return $curl_handle;
    }

    public function process_response($curl_handle = null, $response = null)
    {
        if ($curl_handle && $response)
        {
            $this->curl_handle = $curl_handle;
            $this->response = $response;
        }

        if (is_resource($this->curl_handle))
        {
            $header_size = curl_getinfo($this->curl_handle, CURLINFO_HEADER_SIZE);
            $this->response_headers = substr($this->response, 0, $header_size);
            $this->response_body = substr($this->response, $header_size);
            $this->response_code = curl_getinfo($this->curl_handle, CURLINFO_HTTP_CODE);
            $this->response_info = curl_getinfo($this->curl_handle);

            $this->response_headers = explode("\r\n\r\n", trim($this->response_headers));
            $this->response_headers = array_pop($this->response_headers);
            $this->response_headers = explode("\r\n", $this->response_headers);
            array_shift($this->response_headers);

            $header_assoc = array();
            foreach ($this->response_headers as $header)
            {
                $kv = explode(': ', $header);
                $header_assoc[strtolower($kv[0])] = isset($kv[1])?$kv[1]:'';
            }

            $this->response_headers = $header_assoc;
            $this->response_headers['_info'] = $this->response_info;
            $this->response_headers['_info']['method'] = $this->method;

            if ($curl_handle && $response)
            {
                return new $this->response_class($this->response_headers, $this->response_body, $this->response_code, $this->curl_handle);
            }
        }

        return false;
    }

    public function send_request($parse = false)
    {
        set_time_limit(0);

        $curl_handle = $this->prep_request();
        $this->response = curl_exec($curl_handle);

        if ($this->response === false)
        {
