            echo " [x] Rejecting message.\n";
            $msg->reject();
        }
    };

    $channel->basic_consume($queueName, $consumerTag, false, false, false, false, $callback);

    register_shutdown_function(function () use ($channel, $connection) {
        $channel->close();
        $connection->close();
    });

    while ($channel->is_consuming()) {
        $channel->wait();
    }
