use GuzzleHttp\Client;

class Ollama {
    private $model;
    private $prompt;
    private $temperature;
    private $stream;
    private $raw;

    public function __construct(string $model, string $prompt, float $temperature, bool $stream, bool $raw) {
        $this->model = $model;
        $this->prompt = $prompt;
        $this->temperature = $temperature;
        $this->stream = $stream;
        $this->raw = $raw;
    }

    public function ask(): string {
        $client = new Client([
            'base_uri' => 'https://api.ollama.com/',
            'timeout'  => 2.0,
        ]);

        $response = $client->request('POST', 'generate', [
            'json' => [
                'model' => $this->model,
                'prompt' => $this->prompt,
                'temperature' => $this->temperature,
                'stream' => $this->stream,
                'raw' => $this->raw
            ]
        ]);

        return $response->getBody()->getContents();
    }
}
