<?php

use GuzzleHttp\Client;
use GuzzleHttp\Exception\RequestException;

class EpayHttpClient {
    private $epay;
    private $client;

    public function __construct(array $epayConfig) {
        $this->epay = $epayConfig;
        $this->client = new Client();
    }

    public function sendPaymentRequest(array $data): array {
        try {
            $response = $this->client->request('POST', $this->epay['api_url'], [
                'json' => $data,
                'headers' => [
                    'Authorization' => 'Bearer ' . $this->epay['api_key'],
                    'Content-Type' => 'application/json'
                ]
            ]);

            $responseData = json_decode($response->getBody(), true);

            if ($response->getStatusCode() == 200 && isset($responseData['payment_url'])) {
                return ['success' => true, 'payment_url' => $responseData['payment_url']];
            } else {
                return ['success' => false, 'message' => 'Invalid response from the API'];
            }
        } catch (RequestException $e) {
            return ['success' => false, 'message' => $e->getMessage()];
        }
    }
}
