class CI_Trackback {
    public $charset = 'UTF-8';
    public $data = array();
    public $convert_ascii = TRUE;
    public $response = '';
    public $error_msg = array();

    public function process($url, $data) {
        $url_parts = parse_url($url);
        if (!isset($url_parts['host']) || !isset($url_parts['path'])) {
            $this->set_error('Invalid URL');
            return FALSE;
        }

        $port = isset($url_parts['port']) ? $url_parts['port'] : 80;
        $path = $url_parts['path'];
        if (isset($url_parts['query'])) {
            $path .= '?' . $url_parts['query'];
        }

        $fp = @fsockopen($url_parts['host'], $port, $errno, $errstr, 15);
        if (!$fp) {
            $this->set_error("Could not open socket: $errstr ($errno)");
            return FALSE;
        }

        $headers = "POST $path HTTP/1.1\r\n";
        $headers .= "Host: " . $url_parts['host'] . "\r\n";
        $headers .= "Content-Type: application/x-www-form-urlencoded\r\n";
        $headers .= "Content-Length: " . strlen($data) . "\r\n";
        $headers .= "Connection: Close\r\n\r\n";

        fwrite($fp, $headers . $data);
        $this->response = '';

        while (!feof($fp)) {
            $this->response .= fgets($fp, 128);
        }

        fclose($fp);

        if (strpos($this->response, '200 OK') === FALSE) {
            $this->set_error('Failed to send trackback');
            return FALSE;
        }

        return TRUE;
    }

    public function get_id($url) {
        if (preg_match("#[0-9]+$#", $url, $matches)) {
            return $matches[0];
        }
        return FALSE;
    }

    public function set_error($msg) {
        $this->error_msg[] = $msg;
    }
}
