function fetch_http_content($params, $template = null) {
    $url = parse_url($params['file']);
    $host = $url['host'];
    $path = isset($url['path']) ? $url['path'] : '/';
    $port = isset($url['port']) ? $url['port'] : 80;
    $query = isset($url['query']) ? '?' . $url['query'] : '';
    $scheme = isset($url['scheme']) ? $url['scheme'] : 'http';

    if ($scheme !== 'http') {
        trigger_error('Only HTTP scheme is supported.', E_USER_NOTICE);
        return;
    }

    $proxy_host = isset($params['proxy_host']) ? $params['proxy_host'] : null;
    $proxy_port = isset($params['proxy_port']) ? $params['proxy_port'] : 8080;

    $socket_host = $proxy_host ?: $host;
    $socket_port = $proxy_host ? $proxy_port : $port;

    $fp = fsockopen($socket_host, $socket_port, $errno, $errstr, isset($params['timeout']) ? $params['timeout'] : 30);

    if (!$fp) {
        trigger_error("Could not connect to $socket_host:$socket_port ($errno: $errstr)", E_USER_NOTICE);
        return;
    }

    $request = "GET " . ($proxy_host ? "$scheme://$host$path$query" : "$path$query") . " HTTP/1.1\r\n";
    $request .= "Host: $host\r\n";

    if (isset($params['user']) && isset($params['pass'])) {
        $request .= "Authorization: Basic " . base64_encode($params['user'] . ':' . $params['pass']) . "\r\n";
    }

    if (isset($params['accept'])) {
        $request .= "Accept: " . $params['accept'] . "\r\n";
    }

    if (isset($params['header'])) {
        if (is_array($params['header'])) {
            foreach ($params['header'] as $header) {
                $request .= "$header\r\n";
            }
        } else {
            $request .= $params['header'] . "\r\n";
        }
    }

    if (isset($params['agent'])) {
        $request .= "User-Agent: " . $params['agent'] . "\r\n";
    }

    if (isset($params['referer'])) {
        $request .= "Referer: " . $params['referer'] . "\r\n";
    }

    $request .= "Connection: close\r\n\r\n";

    fwrite($fp, $request);

    $response = '';
    $headers = [];
    $in_headers = true;

    while (!feof($fp)) {
        $line = fgets($fp, 4096);
        if ($in_headers && trim($line) === '') {
            $in_headers = false;
        } elseif ($in_headers) {
            $headers[] = $line;
        } else {
            $response .= $line;
        }
    }

    fclose($fp);

    if (isset($params['assign_headers'])) {
        $template->assign($params['assign_headers'], $headers);
    }

    if (isset($params['assign'])) {
        $template->assign($params['assign'], $response);
    } else {
        return $response;
    }
}
