function dfsockopen($url, $limit = 0, $post = '', $cookie = '', $bysocket = false, $ip = '', $timeout = 15, $block = true, $encodetype = 'URLENCODE', $allowcurl = true, $position = 0, $files = array()) {
    $url = str_replace(' ', '%20', $url);
    $ishttps = substr($url, 0, 5) == 'https' ? true : false;
    $url = substr($url, stripos($url, '//') + 2);
    $pos = strpos($url, '/');
    if ($pos === false) {
        $pos = strlen($url);
    }
    $host = substr($url, 0, $pos);
    $uri = substr($url, $pos);
    $port = 80;
    if (stripos($host, ':') !== false) {
        list($host, $port) = explode(':', $host);
    }
    if ($ishttps && extension_loaded('openssl')) {
        $port = 443;
    }
    if ($ip != '') {
        $host = $ip;
    }
    $post = (is_array($post)) ? http_build_query($post) : $post;
    $out = "GET $uri HTTP/1.0\r\n";
    if ($post != '') {
        $out = "POST $uri HTTP/1.0\r\n";
        if ($encodetype == 'URLENCODE') {
            $out .= "Content-Type: application/x-www-form-urlencoded\r\n";
            $out .= "Content-Length: " . strlen($post) . "\r\n";
        } else {
            $boundary = '----WebKitFormBoundary' . md5(uniqid());
            $out .= "Content-Type: multipart/form-data; boundary=$boundary\r\n";
            $postdata = '';
            foreach ($post as $key => $value) {
                $postdata .= "--$boundary\r\n";
                $postdata .= "Content-Disposition: form-data; name=\"$key\"\r\n\r\n";
                $postdata .= "$value\r\n";
            }
            foreach ($files as $name => $path) {
                $postdata .= "--$boundary\r\n";
                $postdata .= "Content-Disposition: form-data; name=\"$name\"; filename=\"" . basename($path) . "\"\r\n";
                $postdata .= "Content-Type: application/octet-stream\r\n\r\n";
                $postdata .= file_get_contents($path) . "\r\n";
            }
            $postdata .= "--$boundary--\r\n";
            $out .= "Content-Length: " . strlen($postdata) . "\r\n";
            $post = $postdata;
        }
    }
    $out .= "Host: $host\r\n";
    if ($cookie != '') {
        $out .= "Cookie: $cookie\r\n";
    }
    $out .= "Connection: Close\r\n\r\n";
    if ($post != '') {
        $out .= $post;
    }
    if ($allowcurl && function_exists('curl_init')) {
        $ch = curl_init();
        curl_setopt($ch, CURLOPT_URL, ($ishttps ? 'https://' : 'http://') . $host . $uri);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($ch, CURLOPT_TIMEOUT, $timeout);
        curl_setopt($ch, CURLOPT_HEADER, true);
        curl_setopt($ch, CURLOPT_POSTFIELDS, $post);
        curl_setopt($ch, CURLOPT_HTTPHEADER, array("Host: $host", "Connection: Close"));
        if ($cookie != '') {
            curl_setopt($ch, CURLOPT_COOKIE, $cookie);
        }
        if ($ishttps) {
            curl_setopt($ch, CURLOPT_SSL_VERIFYPEER, false);
            curl_setopt($ch, CURLOPT_SSL_VERIFYHOST, false);
        }
        $response = curl_exec($ch);
        $header_size = curl_getinfo($ch, CURLINFO_HEADER_SIZE);
        $header = substr($response, 0, $header_size);
        $body = substr($response, $header_size);
        curl_close($ch);
        $GLOBALS['filesockheader'] = $header;
        if (strpos($header, 'HTTP/1.1 200 OK') === false) {
            return '';
        }
        return $limit > 0 ? substr($body, 0, $limit) : $body;
    } else {
        $fp = fsockopen(($ishttps ? 'ssl://' : '') . $host, $port, $errno, $errstr, $timeout);
        if (!$fp) {
            return '';
        }
        stream_set_blocking($fp, $block);
        fwrite($fp, $out);
        $status = stream_get_meta_data($fp);
        $header = '';
        while (!feof($fp) && !$status['timed_out']) {
            $header .= fgets($fp, 1024);
            $status = stream_get_meta_data($fp);
            if (strpos($header, "\r\n\r\n") !== false) {
                break;
            }
        }
        $header = trim($header);
        $headers = explode("\r\n", $header);
        $status_line = array_shift($headers);
        list($protocol, $status_code, $status_message) = explode(' ', $status_line, 3);
        if ($status_code != 200) {
            fclose($fp);
            return '';
        }
        $body = '';
        while (!feof($fp) && !$status['timed_out']) {
            $body .= fread($fp, 1024);
            $status = stream_get_meta_data($fp);
            if ($limit > 0 && strlen($body) >= $limit) {
                break;
            }
        }
        fclose($fp);
        $GLOBALS['filesockheader'] = $header;
        return $body;
    }
