    protected $_redis;
    protected $_key_prefix = '';
    protected $_lock_key = '';
    protected $_key_exists = false;
    protected $_config = [];
    protected $_session_id = '';
    protected $_fingerprint = '';

    public function __construct(&$params) {
        $this->_config = $params;
        $this->_redis = new Redis();
    }

    public function open($save_path, $name) {
        return $this->_redis->connect(
            $this->_config['host'],
            $this->_config['port'],
            $this->_config['timeout']
        );
    }

    public function read($session_id) {
        $this->_session_id = $session_id;
        $this->_lock_key = $this->_key_prefix . ':lock:' . $session_id;
        $this->_key_exists = $this->_redis->exists($this->_key_prefix . ':' . $session_id);

        if ($this->_key_exists && $this->_get_lock($session_id)) {
            $data = $this->_redis->get($this->_key_prefix . ':' . $session_id);
            $this->_fingerprint = md5($data);
            $this->_release_lock();
            return $data;
        }
        return '';
    }

    public function write($session_id, $session_data) {
        if ($this->_session_id !== $session_id) {
            $this->read($session_id);
        }

        if ($this->_get_lock($session_id)) {
            $this->_redis->set($this->_key_prefix . ':' . $session_id, $session_data);
            $this->_redis->expire($this->_key_prefix . ':' . $session_id, $this->_config['expiration']);
            $this->_release_lock();
            return true;
        }
        return false;
    }

    public function close() {
        return $this->_redis->close();
    }

    public function destroy($session_id) {
        if ($this->_get_lock($session_id)) {
            $this->_redis->del($this->_key_prefix . ':' . $session_id);
            $this->_release_lock();
            return true;
        }
        return false;
    }

    public function gc($maxlifetime) {
        // Not implemented for Redis
        return true;
    }

    protected function _get_lock($session_id) {
        return $this->_redis->setnx($this->_lock_key, 1);
    }

    protected function _release_lock() {
        return $this->_redis->del($this->_lock_key);
    }

    protected function _fail() {
        return false;
    }

    protected function _success() {
        return true;
    }
}
