        {
            $this->_redis->pconnect($config['host'], $config['port'], $config['timeout']);
        }
        else
        {
            $this->_redis->connect($config['host'], $config['port'], $config['timeout']);
        }

        if ($config['password'])
        {
            $this->_redis->auth($config['password']);
        }

        if ($config['database'] !== 0)
        {
            $this->_redis->select($config['database']);
        }
    }

    public function get($key)
    {
        $value = $this->_redis->get($key);

        if ($value !== FALSE && isset($this->_serialized[$key]))
        {
            return unserialize($value);
        }

        return $value;
    }

    public function save($id, $data, $ttl = 60, $raw = FALSE)
    {
        if ($raw === FALSE)
        {
            $value = serialize($data);
            $this->_serialized[$id] = TRUE;
        }
        else
        {
            $value = $data;
            unset($this->_serialized[$id]);
        }

        return $this->_redis->setex($id, $ttl, $value);
    }

    public function delete($key)
