class PhpiredisSocketConnection {
    private $reader;
    private $parameters;

    public function __construct(ParametersInterface $parameters) {
        $this->parameters = $parameters;
        $this->assertExtensions();
        $this->reader = $this->createReader();
    }

    public function __destruct() {
        if (is_resource($this->reader)) {
            phpiredis_reader_free($this->reader);
        }
    }

    private function assertExtensions() {
        if (!extension_loaded('redis') || !extension_loaded('phpiredis')) {
            throw new RuntimeException('Required extensions (redis, phpiredis) are not loaded.');
        }
    }

    private function createReader() {
        return phpiredis_reader_create(phpiredis_reader_type_array);
    }

    public function getStatusHandler() {
        return function ($response) {
            return ['status' => $response];
        };
    }

    public function getErrorHandler() {
        return function ($response) {
            return ['error' => $response];
        };
    }

    private function createResource() {
        $socket = socket_create(AF_INET, SOCK_STREAM, SOL_TCP);
        if (!$socket) {
            throw new RuntimeException('Failed to create socket.');
        }
        $this->connectWithTimeout($socket, $this->parameters->getHost(), $this->parameters);
        return $socket;
    }

    private function connectWithTimeout($socket, $address, ParametersInterface $parameters) {
        $timeout = $parameters->getTimeout();
        socket_set_nonblock($socket);
        $result = @socket_connect($socket, $address, $parameters->getPort());
        if ($result === false) {
            $errno = socket_last_error($socket);
            if ($errno !== SOCKET_EINPROGRESS && $errno !== SOCKET_EALREADY) {
                throw new RuntimeException('Socket connect failed: ' . socket_strerror($errno));
            }
        }
        $select = [
            ['s' => $socket],
            [],
            ['s' => $socket],
            $timeout
        ];
        if (@socket_select($select[0], $select[1], $select[2], $timeout) === false) {
            throw new RuntimeException('Socket select failed.');
        }
        if (in_array($socket, $select[2])) {
            throw new RuntimeException('Socket connect failed.');
        }
        socket_set_block($socket);
    }

    public function write(string $buffer) {
        $written = socket_write($this->socket, $buffer, strlen($buffer));
        if ($written === false) {
            throw new RuntimeException('Failed to write to socket.');
        }
    }

    public function read() {
        $buffer = '';
        while (true) {
            $data = socket_read($this->socket, 4096);
            if ($data === false) {
                throw new RuntimeException('Failed to read from socket.');
            }
            $buffer .= $data;
            phpiredis_reader_feed($this->reader, $buffer);
            if (phpiredis_reader_get_state($this->reader) === PHPIREDIS_READER_STATE_COMPLETE) {
                return phpiredis_reader_get_reply($this->reader);
            }
        }
    }

    public function writeRequest(CommandInterface $command) {
        $this->write($command->toRedisProtocol());
    }
}
