class Server {
    private $host;
    private $port;
    private $output;
    private $stop = false;
    private $socket;
    private $clientSockets = [];
    private $afterCallback;
    private $tickerCallback;
    private $routerCallback;

    public function __construct(string $host, int $port, OutputInterface $output) {
        $this->host = $host;
        $this->port = $port;
        $this->output = $output;
        $this->checkRequiredExtensionsExists();
    }

    private function checkRequiredExtensionsExists() {
        if (!extension_loaded('sockets')) {
            throw new Exception('The sockets extension is required.');
        }
    }

    public function run() {
        $this->socket = $this->createServerSocket();
        $this->updatePort();

        if ($this->afterCallback) {
            call_user_func($this->afterCallback);
        }

        while (!$this->stop) {
            $this->acceptNewConnections();
            $this->handleClientRequests();

            if ($this->tickerCallback) {
                call_user_func($this->tickerCallback);
            }
        }

        foreach ($this->clientSockets as $key => $clientSocket) {
            $this->closeClientSocket($clientSocket, $key);
        }
        fclose($this->socket);
    }

    private function createServerSocket() {
        $socket = stream_socket_server("tcp://{$this->host}:{$this->port}", $errno, $errstr);

        if (!$socket) {
            throw new Exception("Could not bind to {$this->host}:{$this->port}. Error: $errstr ($errno)");
        }

        stream_set_blocking($socket, 0);
        return $socket;
    }

    private function updatePort() {
        $address = stream_socket_get_name($this->socket, false);
        list($this->host, $this->port) = explode(':', $address);
    }

    private function acceptNewConnections() {
        $clientSocket = @stream_socket_accept($this->socket, 0, $peername);

        if ($clientSocket) {
            stream_set_blocking($clientSocket, 0);
            $this->clientSockets[] = $clientSocket;
            $this->output->log("New connection from $peername");
        }
    }

    private function handleClientRequests() {
        foreach ($this->clientSockets as $key => $clientSocket) {
            if (is_resource($clientSocket) && !feof($clientSocket)) {
                $request = $this->readClientRequest($clientSocket);
                if ($request) {
                    $parsedRequest = $this->parseRequest($request);
                    $response = new Response(200, "Echo: " . json_encode($parsedRequest));
                    $this->sendResponse($clientSocket, $response);
                } else {
                    $this->closeClientSocket($clientSocket, $key);
                }
            }
        }
    }

    private function readClientRequest($clientSocket): string {
        $buffer = '';
        do {
            $data = fread($clientSocket, 1024);
            if ($data === false) {
                break;
            }
            $buffer .= $data;
        } while (!feof($clientSocket));

        return $buffer;
    }

    private function parseRequest(string $request): array {
        // Simple parsing for demonstration purposes
        $lines = explode("\r\n", $request);
        $firstLine = explode(' ', $lines[0]);
        $path = $firstLine[1];
        $payload = json_decode(trim(end($lines)), true);
        return ['path' => $path, 'payload' => $payload];
    }

    private function sendResponse($clientSocket, Response $response) {
        $statusLine = "HTTP/1.1 " . $response->getStatus() . " OK\r\n";
        $headers = "Content-Type: application/json\r\n";
        $body = json_encode(['status' => $response->getStatus(), 'payload' => $response->getPayload()]);
        $contentLength = "Content-Length: " . strlen($body) . "\r\n";
        $responseString = $statusLine . $headers . $contentLength . "\r\n" . $body;

        fwrite($clientSocket, $responseString);
    }

    private function closeClientSocket($clientSocket, int $key) {
        fclose($clientSocket);
        unset($this->clientSockets[$key]);
        $this->output->log("Connection closed");
    }

