class DefaultPhpProcess {
    private $tempFile = null;
    private $stdin = null;
    private $env = null;
    private $timeout = null;

    public function runJob(string $job, array $settings = []): array {
        $this->stdin = $job;
        $this->env = $settings['env'] ?? [];
        $this->timeout = $settings['timeout'] ?? null;
        return $this->runProcess($job, $settings);
    }

    public function getHandles(): array {
        // Placeholder for additional stream handles
        return [];
    }

    private function runProcess(string $job, array $settings): array {
        $descriptorspec = [
            0 => $this->useTemporaryFile() ? fopen($this->tempFile, 'r') : ['pipe', 'r'],
            1 => ['pipe', 'w'],
            2 => ['pipe', 'w']
        ];

        $process = proc_open($job, $descriptorspec, $pipes, null, $this->env);

        if (is_resource($process)) {
            if (!$this->useTemporaryFile()) {
                $this->process($pipes[0], $job);
            }
            fclose($pipes[0]);

            $stdout = stream_get_contents($pipes[1]);
            fclose($pipes[1]);

            $stderr = stream_get_contents($pipes[2]);
            fclose($pipes[2]);

            proc_close($process);

            return [
                'stdout' => $stdout,
                'stderr' => $stderr
            ];
        }

        return [
            'stdout' => '',
            'stderr' => 'Failed to start process'
        ];
    }

    private function process(resource $pipe, string $job): void {
        fwrite($pipe, $job);
        fflush($pipe);
    }

    public function cleanup(): void {
        if ($this->tempFile && file_exists($this->tempFile)) {
            unlink($this->tempFile);
        }
    }

    private function useTemporaryFile(): bool {
        return strlen($this->stdin) > 1024; // Example condition for using temporary files
    }
}
