                $this->_stdOut = implode("\n", $output);
            } else {
                exec($command, $output, $this->_exitCode);
                $this->_stdOut = implode("\n", $output);
            }
        } else {
            $descriptorspec = [
                0 => ["pipe", "r"],
                1 => ["pipe", "w"],
                2 => $this->captureStdErr ? ["pipe", "w"] : STDERR
            ];
            $process = proc_open($command, $descriptorspec, $pipes, $this->procCwd, $this->procEnv, $this->procOptions);
