abstract class AbstractPhpProcess {
    abstract protected function getHandles();
    abstract protected function runProcess(string $job, array $settings);
    abstract protected function process(resource $pipe, string $job);
    abstract protected function cleanup();
    abstract protected function useTemporaryFile(): bool;
}

class DefaultPhpProcess extends AbstractPhpProcess {
    protected $tempFile;
    protected $stdin;
    protected $env;
    protected $timeout;

    public function __construct($stdin = null, $env = [], $timeout = 30) {
        $this->stdin = $stdin;
        $this->env = $env;
        $this->timeout = $timeout;
    }

    public function runJob(string $job, array $settings = []): array {
        if ($this->useTemporaryFile()) {
            $this->tempFile = tempnam(sys_get_temp_dir(), 'job_input');
            file_put_contents($this->tempFile, $job);
            $this->stdin = $this->tempFile;
        }
        return $this->runProcess($job, $settings);
    }

    protected function getHandles(): array {
        return [];
    }

    protected function runProcess(string $job, array $settings): array {
        $descriptorspec = [
            0 => ($this->stdin === null) ? STDIN : fopen($this->stdin, 'r'),
            1 => ['pipe', 'w'],
            2 => ['pipe', 'w']
        ];

        $handles = $this->getHandles();
        foreach ($handles as $key => $handle) {
            $descriptorspec[$key] = $handle;
        }

        $process = proc_open('php', $descriptorspec, $pipes, null, $this->env);

        if (is_resource($process)) {
            $this->process($pipes[0], $job);
            fclose($pipes[0]);

            $output = stream_get_contents($pipes[1]);
            fclose($pipes[1]);

            $error = stream_get_contents($pipes[2]);
            fclose($pipes[2]);

            proc_close($process);

            return [$output, $error];
        }

        return ['', 'Failed to start process'];
    }

    protected function process(resource $pipe, string $job): void {
        fwrite($pipe, $job);
    }

    protected function cleanup(): void {
        if ($this->tempFile !== null && file_exists($this->tempFile)) {
            unlink($this->tempFile);
        }
    }

    protected function useTemporaryFile(): bool {
        return false;
    }
}
