        $this->appCertSn = $appCertSn;
    }

    public function doTransfer(float $totalFee, string $outTradeNo, string $account, string $realName, string $remark = '') {
        $params = [
            'app_id' => $this->appId,
            'method' => 'alipay.fund.trans.toaccount.transfer',
            'format' => 'JSON',
            'charset' => $this->charset,
            'sign_type' => 'RSA2',
            'timestamp' => date('Y-m-d H:i:s'),
            'version' => '1.0',
            'biz_content' => json_encode([
                'out_biz_no' => $outTradeNo,
                'payee_type' => 'ALIPAY_LOGONID',
                'payee_account' => $account,
                'amount' => $totalFee,
                'payer_show_name' => $realName,
                'remark' => $remark
            ])
        ];

        $params['sign'] = $this->generateSign($params);
        return json_decode($this->curlPost('https://openapi.alipay.com/gateway.do', $params), true);
    }

    private function curlPost(string $url, array $postData, array $options = []) {
        $ch = curl_init();
        curl_setopt($ch, CURLOPT_URL, $url);
        curl_setopt($ch, CURLOPT_POST, 1);
        curl_setopt($ch, CURLOPT_POSTFIELDS, http_build_query($postData));
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);
        curl_setopt($ch, CURLOPT_SSL_VERIFYPEER, true);
        curl_setopt($ch, CURLOPT_SSL_VERIFYHOST, 2);
        curl_setopt($ch, CURLOPT_CAINFO, __DIR__ . '/alipayRootCert.crt');
        curl_setopt($ch, CURLOPT_CAPATH, __DIR__);
        curl_setopt_array($ch, $options);
        $response = curl_exec($ch);
        curl_close($ch);
        return $response;
    }

    private function generateSign(array $params, string $signType = "RSA2") {
        ksort($params);
        $stringToBeSigned = $this->getSignContent($params);
        return $this->sign($stringToBeSigned, $signType);
    }

    private function sign(string $data, string $signType = "RSA2") {
        $privateKey = openssl_pkey_get_private($this->rsaPrivateKey);
        openssl_sign($data, $signature, $privateKey, OPENSSL_ALGO_SHA256);
        openssl_free_key($privateKey);
        return base64_encode($signature);
    }

    private function checkEmpty($value) {
        return empty($value);
    }

    private function getSignContent(array $params) {
        $content = '';
        foreach ($params as $key => $value) {
            if ($this->checkEmpty($value)) continue;
            $content .= $key . '=' . $value . '&';
        }
        return rtrim($content, '&');
    }

    private function characet($data, $targetCharset) {
        if (!empty($data)) {
            $fileType = mb_detect_encoding($data, ['UTF-8', 'GBK', 'GB2312', 'ISO-8859-1']);
            if (stripos($fileType, 'gbk') !== false || stripos($fileType, 'gb2312') !== false) {
                $data = iconv('GBK', $targetCharset, $data);
            } elseif (stripos($fileType, 'big5') !== false) {
                $data = iconv('BIG5', $targetCharset, $data);
            } elseif (stripos($fileType, 'ISO-8859-1') !== false) {
                $data = iconv('ISO-8859-1', $targetCharset, $data);
            }
        }
        return $data;
    }
