<?php

use GuzzleHttp\Client;
use GuzzleHttp\Exception\RequestException;

class Config {
    public $timeout;
    public $serviceName;
    public $processUrl;
    public $queryUrl;
    public $processNotifyUrl;

    public function __construct($timeout, $serviceName, $processUrl, $queryUrl, $processNotifyUrl) {
        $this->timeout = $timeout;
        $this->serviceName = $serviceName;
        $this->processUrl = $processUrl;
        $this->queryUrl = $queryUrl;
        $this->processNotifyUrl = $processNotifyUrl;
    }
}

class Pretreat {
    private $config;
    private $client;

    public function __construct(Config $config) {
        $this->config = $config;
        $this->client = new Client([
            'base_uri' => $this->config->serviceName,
            'timeout'  => $this->config->timeout,
        ]);

        if (empty($this->config->processNotifyUrl)) {
            throw new Exception("processNotifyUrl is not configured.");
        }
    }

    public function process(array $tasks, array $optionalParams = []): array {
        $params = array_merge([
            'tasks' => $tasks,
            'notify_url' => $this->config->processNotifyUrl,
        ], $optionalParams);

        try {
            $response = $this->client->request('POST', $this->config->processUrl, [
                'json' => $params,
            ]);

            return json_decode($response->getBody()->getContents(), true);
        } catch (RequestException $e) {
            return ['error' => $e->getMessage()];
        }
    }

    public function query(array $taskIds, string $path): array|bool {
        $params = [
            'task_ids' => $taskIds,
        ];

        try {
            $response = $this->client->request('GET', $this->config->queryUrl . $path, [
                'query' => $params,
            ]);

            $data = json_decode($response->getBody()->getContents(), true);
            return $data['success'] ? $data['tasks'] : false;
        } catch (RequestException $e) {
            return false;
        }
    }
}
