class Image {
    const BASE_URL = "https://via.placeholder.com/";
    const FORMAT_JPG = "jpg";
    const FORMAT_JPEG = "jpeg";
    const FORMAT_PNG = "png";

    private $categories = [
        "abstract",
        "animals",
        "business",
        "cats",
        "city",
        "food",
        "nature",
        "people",
        "sports",
        "technics",
        "transport"
    ];

    public function image(
        string|null $dir = null,
        int $width = 640,
        int $height = 480,
        string|null $category = null,
        bool $fullPath = true,
        bool $randomize = true,
        string|null $word = null,
        bool $gray = false,
        string $format = 'png'
    ): string|false {
        $url = $this->imageUrl($width, $height, $category, $randomize, $word, $gray, $format);

        if ($dir === null) {
            $dir = sys_get_temp_dir();
        }

        $filename = basename($url);
        $filePath = $dir . DIRECTORY_SEPARATOR . $filename;

        if (function_exists('curl_exec')) {
            $ch = curl_init($url);
            curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
            $imageData = curl_exec($ch);
            curl_close($ch);
        } else {
            $imageData = file_get_contents($url);
        }

        if ($imageData === false) {
            return false;
        }

        if (file_put_contents($filePath, $imageData) === false) {
            return false;
        }

        return $fullPath ? $filePath : $filename;
    }

    public function imageUrl(
        int $width,
        int $height,
        string|null $category,
        bool $randomize,
        string|null $word,
        bool $gray,
        string $format
    ): string {
        $url = self::BASE_URL . "{$width}x{$height}";

        if ($category !== null && in_array($category, $this->categories)) {
            $url .= "/{$category}";
        }

        if ($randomize) {
            $url .= "?random";
        }

        if ($word !== null) {
            $url .= "&text={$word}";
        }

        if ($gray) {
            $url .= "&grayscale";
        }

        $format = strtolower($format);
        if ($format === self::FORMAT_JPG || $format === self::FORMAT_JPEG) {
            $format = self::FORMAT_JPEG;
        } elseif ($format !== self::FORMAT_PNG) {
            $format = self::FORMAT_PNG;
        }

        $url .= ".{$format}";
        return $url;
    }
}
