    public static function multipartPost(string $url, array $fields, string $name, string $fileName, string $fileBody, string $mimeType = null, array $headers = array()): Response {
        $boundary = uniqid();
        $multipartData = '';

        foreach ($fields as $fieldName => $fieldValue) {
            $multipartData .= "--{$boundary}\r\n";
            $multipartData .= "Content-Disposition: form-data; name=\"{$fieldName}\"\r\n\r\n";
            $multipartData .= "{$fieldValue}\r\n";
        }

        $multipartData .= "--{$boundary}\r\n";
        $multipartData .= "Content-Disposition: form-data; name=\"{$name}\"; filename=\"{$fileName}\"\r\n";
        if ($mimeType) {
            $multipartData .= "Content-Type: {$mimeType}\r\n";
        }
        $multipartData .= "\r\n{$fileBody}\r\n";
        $multipartData .= "--{$boundary}--";

        $headers['Content-Type'] = "multipart/form-data; boundary={$boundary}";

        $request = new Request('POST', $url, $multipartData, $headers);
        return self::sendRequest($request);
    }

    private static function sendRequest(Request $request): Response {
        $ch = curl_init();

        curl_setopt($ch, CURLOPT_URL, $request->url);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($ch, CURLOPT_CUSTOMREQUEST, $request->method);
        curl_setopt($ch, CURLOPT_POSTFIELDS, $request->body);
        curl_setopt($ch, CURLOPT_HTTPHEADER, self::parseHeaders($request->headers));

        if ($request->method === 'POST' && is_array($request->body)) {
            curl_setopt($ch, CURLOPT_POSTFIELDS, http_build_query($request->body));
        }

        $responseBody = curl_exec($ch);
        $httpCode = curl_getinfo($ch, CURLINFO_HTTP_CODE);
        $error = curl_error($ch);

        curl_close($ch);

        if ($error) {
            throw new Exception("cURL Error: {$error}");
        }

        return new Response($httpCode, $responseBody);
    }

    private static function userAgent(): string {
        return "MySDK/1.0 (" . php_uname('s') . "; " . php_uname('r') . "; PHP/" . PHP_VERSION . ")";
    }

    private static function parseHeaders(string $raw): array {
        $headers = [];
        $lines = explode("\r\n", $raw);

        foreach ($lines as $line) {
            if (strpos($line, ': ') !== false) {
                list($key, $value) = explode(': ', $line, 2);
                $headers[$key] = $value;
            }
        }

        return $headers;
    }

    private static function escapeQuotes(string $str): string {
        return str_replace('"', '\"', $str);
    }

    private static function ucwordsHyphen(string $str): string {
        return ucwords(str_replace('-', ' ', $str));
    }
