        return strlen($data);
    });

    // Execute cURL session
    $result = curl_exec($ch);

    // Check for errors
    if ($result === false) {
        $errno = curl_errno($ch);
        $error = curl_error($ch);
        setcookie('curl_error', $error, time() + 3600, '/');
