    public static function multipartPost(
        string $url,
        array $fields,
        string $name,
        string $fileName,
        string $fileBody,
        string $mimeType = null,
        array $headers = array()
    ): Response {
        $boundary = md5(uniqid());
        $header = "Content-Type: multipart/form-data; boundary=$boundary";
        $fields[$name] = [
            'name' => $fileName,
            'type' => $mimeType ?? mime_content_type($fileName),
            'tmp_name' => $fileName,
            'error' => UPLOAD_ERR_OK,
            'size' => strlen($fileBody)
        ];

        $body = '';
        foreach ($fields as $key => $value) {
            if (is_array($value)) {
                $body .= "--$boundary\r\n";
                $body .= "Content-Disposition: form-data; name=\"$key\"; filename=\"{$value['name']}\"\r\n";
                $body .= "Content-Type: {$value['type']}\r\n\r\n";
                $body .= $value['tmp_name'];
            } else {
                $body .= "--$boundary\r\n";
                $body .= "Content-Disposition: form-data; name=\"$key\"\r\n\r\n";
                $body .= $value;
            }
            $body .= "\r\n";
        }
        $body .= "--$boundary--";

        $headers[] = $header;

        return self::sendRequest(new Request('POST', $url, $body, $headers));
    }

    public static function sendRequest(Request $request): Response {
