    }

    public function init() {
        return [
            'api' => defined('ELFINDER_ONLINE_CONVERT_API_KEY') && ELFINDER_ONLINE_CONVERT_API_KEY !== '',
            'curl' => function_exists('curl_init')
        ];
    }

    public function api() {
        $apiKey = ELFINDER_ONLINE_CONVERT_API_KEY;
        $category = $this->options['category'] ?? '';
        $convert = $this->options['convert'] ?? '';
        $options = $this->options['options'] ?? [];
        $source = $this->options['source'] ?? '';
        $filename = $this->options['filename'] ?? '';
        $mime = $this->options['mime'] ?? '';
        $jobid = $this->options['jobid'] ?? '';

        if (!$apiKey || !$category || !$convert || !$source) {
            return ['error' => 'Missing required parameters'];
        }

        $ch = curl_init();
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);
        curl_setopt($ch, CURLOPT_POST, 1);

        if ($jobid) {
            // Check job status
            $url = "https://api.online-convert.com/v2/jobs/{$jobid}";
            curl_setopt($ch, CURLOPT_URL, $url);
            curl_setopt($ch, CURLOPT_HTTPHEADER, [
                "Authorization: Bearer {$apiKey}"
            ]);
        } else {
            // Create new conversion job
            $url = "https://api.online-convert.com/v2/jobs";
            $data = [
                'input' => [
                    'url' => $source
                ],
                'output' => [
                    'format' => $convert,
                    'preset' => $options
                ]
            ];
            curl_setopt($ch, CURLOPT_URL, $url);
            curl_setopt($ch, CURLOPT_POSTFIELDS, json_encode($data));
            curl_setopt($ch, CURLOPT_HTTPHEADER, [
                "Content-Type: application/json",
                "Authorization: Bearer {$apiKey}"
            ]);
        }

        $response = curl_exec($ch);
        curl_close($ch);

        if ($response === false) {
            return ['error' => 'cURL error'];
        }

        $responseData = json_decode($response, true);
        if (json_last_error() !== JSON_ERROR_NONE) {
            return ['error' => 'Invalid JSON response'];
        }

        return $responseData;
    }
