        return (new Httpie())->method('PUT')->url($url);
    }

    public static function delete(string $url): Httpie {
        return (new Httpie())->method('DELETE')->url($url);
    }

    public function method(string $method): self {
        $this->method = strtoupper($method);
        return $this;
    }

    public function url(string $url): self {
        $this->url = $url;
        return $this;
    }

    public function query(array $params): self {
        $queryString = http_build_query($params);
        $this->url .= strpos($this->url, '?') === false ? "?$queryString" : "&$queryString";
        return $this;
    }

    public function header(string $header, string $value): self {
        $this->headers[$header] = $value;
        return $this;
    }

    public function body(string $body): self {
        $this->body = $body;
        return $this;
    }

    public function jsonBody(array $data): self {
        $this->body = json_encode($data);
        $this->header('Content-Type', 'application/json');
        return $this;
    }

    public function formBody(array $data): self {
        $this->body = http_build_query($data);
        $this->header('Content-Type', 'application/x-www-form-urlencoded');
        return $this;
    }

    public function setopt(int $key, mixed $value): self {
        $this->curlopts[$key] = $value;
        return $this;
    }

    public function nothrow(bool $on = true): self {
        $this->nothrow = $on;
        return $this;
    }

