function makeHttpRequest($url, $auth = true, $customRequest = null, $postFields = null) {
    $ch = curl_init();
    curl_setopt($ch, CURLOPT_URL, $url);
    curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);

    if ($auth) {
        // Basic Authentication
        curl_setopt($ch, CURLOPT_USERPWD, "username:password");
        // Bearer Token Authentication
        curl_setopt($ch, CURLOPT_HTTPHEADER, array('Authorization: Bearer your_token_here'));
    }

    if ($customRequest) {
        curl_setopt($ch, CURLOPT_CUSTOMREQUEST, $customRequest);
    }

    if ($postFields) {
        curl_setopt($ch, CURLOPT_POSTFIELDS, json_encode($postFields));
        curl_setopt($ch, CURLOPT_HTTPHEADER, array_merge(
            curl_getinfo($ch, CURLINFO_HTTPHEADER),
            array('Content-Type: application/json')
        ));
    }

    // Proxy Configuration
    curl_setopt($ch, CURLOPT_PROXY, "proxy.example.com:8080");

    $response = curl_exec($ch);
    $httpCode = curl_getinfo($ch, CURLINFO_HTTP_CODE);

    if ($httpCode != 200) {
        // Handle error
        return null;
    }

    curl_close($ch);

    $decodedResponse = json_decode($response);
    return $decodedResponse ? $decodedResponse : null;
}
