            "Authorization" => "Bearer " . $OPENAI_API_KEY,
            "Content-Type" => $this->contentTypes["json"]
        ];
    }

    public function sendRequest(string $url, string $method, array $opts = []) {
        $this->baseUrl($url);
        $ch = curl_init($url);
        curl_setopt($ch, CURLOPT_CUSTOMREQUEST, $method);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($ch, CURLOPT_HTTPHEADER, $this->headers);
        curl_setopt($ch, CURLOPT_TIMEOUT, $this->timeout);

        if (!empty($this->proxy)) {
            curl_setopt($ch, CURLOPT_PROXY, $this->proxy);
        }

        if ($method === "POST" && isset($opts["data"])) {
            if ($this->headers["Content-Type"] === $this->contentTypes["json"]) {
                curl_setopt($ch, CURLOPT_POSTFIELDS, json_encode($opts["data"]));
            } elseif ($this->headers["Content-Type"] === $this->contentTypes["form-data"]) {
                $boundary = "------------------------" . substr(md5(time()), 0, 12);
                $this->headers["Content-Type"] .= "; boundary=" . $boundary;
                curl_setopt($ch, CURLOPT_POSTFIELDS, $this->buildFormData($opts["data"], $boundary));
                curl_setopt($ch, CURLOPT_HTTPHEADER, $this->headers);
            }
        }

        if (isset($opts["stream"])) {
            curl_setopt($ch, CURLOPT_WRITEFUNCTION, [$this, 'streamCallback']);
        }

        $response = curl_exec($ch);
        $this->curlInfo = curl_getinfo($ch);
        curl_close($ch);

        return $response;
    }

    private function baseUrl(string &$url) {
        if (!empty($this->customUrl)) {
            $url = $this->customUrl . $url;
        }
    }

    private function buildFormData(array $data, string $boundary): string {
        $body = '';
        foreach ($data as $name => $value) {
            $body .= "--" . $boundary . "\r\n";
            $body .= "Content-Disposition: form-data; name=\"" . $name . "\"\r\n\r\n";
            $body .= $value . "\r\n";
        }
        $body .= "--" . $boundary . "--\r\n";
        return $body;
    }

    public function streamCallback($ch, $data) {
        if (is_callable($this->stream_method)) {
            call_user_func($this->stream_method, $data);
        }
        return strlen($data);
    }
}
