function serveFavicon($url, $cache_time, $default_ico, $basename = 'favicon.ico') {
    $cache_dir = __DIR__ . '/cache/';
    if (!is_dir($cache_dir)) {
        mkdir($cache_dir, 0755, true);
    }

    $cache_file = $cache_dir . md5($url) . '.ico';
    $cache_exists = file_exists($cache_file);
    $cache_valid = $cache_exists && (filemtime($cache_file) + $cache_time > time());

    if ($cache_valid) {
        header('Content-Type: image/x-icon');
        readfile($cache_file);
        return;
    }

    $favicon_url = rtrim($url, '/') . '/' . $basename;
    $ch = curl_init($favicon_url);
    curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
    curl_setopt($ch, CURLOPT_FOLLOWLOCATION, true);
    curl_setopt($ch, CURLOPT_TIMEOUT, 10);
    $icon_data = curl_exec($ch);
    $http_code = curl_getinfo($ch, CURLINFO_HTTP_CODE);
    curl_close($ch);

    if ($http_code == 200 && @getimagesizefromstring($icon_data)) {
        file_put_contents($cache_file, $icon_data);
        header('Content-Type: image/x-icon');
        echo $icon_data;
    } else {
        header('Content-Type: image/x-icon');
        readfile($default_ico);
    }
