    public function __construct(ReCaptchaSocket $socket = null) {
        $this->socket = $socket ?: new ReCaptchaSocket();
    }

    public function submit(ReCaptchaRequestParameters $params) {
        $host = self::RECAPTCHA_HOST;
        $path = self::SITE_VERIFY_PATH;
        $port = 443;

        if (!$this->socket->connect($host, $port)) {
            return self::BAD_REQUEST;
        }

        $postData = http_build_query([
            'secret' => $params->getSecret(),
            'response' => $params->getResponse(),
            'remoteip' => $params->getRemoteIp()
        ]);

        $request = "POST $path HTTP/1.1\r\n";
        $request .= "Host: $host\r\n";
        $request .= "Content-Type: application/x-www-form-urlencoded\r\n";
        $request .= "Content-Length: " . strlen($postData) . "\r\n";
        $request .= "Connection: close\r\n\r\n";
        $request .= $postData;

        if (!$this->socket->write($request)) {
            return self::BAD_REQUEST;
        }

        $response = $this->socket->read();
        $this->socket->close();

        if (!$response) {
            return self::BAD_RESPONSE;
        }

        $headers = [];
        $body = '';
        $inHeaders = true;

        foreach (explode("\r\n", $response) as $line) {
            if ($inHeaders && $line === '') {
                $inHeaders = false;
                continue;
            }
            if ($inHeaders) {
                list($key, $value) = explode(': ', $line, 2);
                $headers[$key] = $value;
            } else {
                $body .= $line;
            }
        }

        if (!isset($headers['Content-Type']) || $headers['Content-Type'] !== 'application/json') {
            return self::BAD_RESPONSE;
        }

        return $body;
    }
}
